import { createSlice, current } from "@reduxjs/toolkit";

export interface EnumDetails {
  value?: string;
}

export interface ServerDetails {
  url: string;
  description: string;
  default?: string;
  enums?: EnumDetails[];
}

const initialState: ServerDetails[] = [];

const serversSlice = createSlice({
  name: "serversSlice",
  initialState,
  reducers: {
    addServer: (state, action) => {},
    editServer: (state, action) => {},
  },
});

const selectServers = (state) => state.upload.value.servers;

// If you're using React, you can dispatch an action to access the state
export const getServerData = () => (dispatch, getState) => {
  const servers = selectServers(getState());
  console.log("Servers:", servers);
  // You can also dispatch actions in this function
  // dispatch(someActionInServersSlice(servers));
};

export const { addServer } = serversSlice.actions;
export const serverDetails = (state: { upload: any }) =>
  state.upload.value.servers;
export default serversSlice.reducer;
