import React, { useState } from "react";
import {
  Card,
  Button,
  InputWithMovingLabel,
} from "intelli-ui-components-library";
import { AddIcon } from "../../../../assets/icons";
import { useAppDispatch, useAppSelector } from "../../../../store/hooks";
import Responses from "./Responses";
import { updatePath } from "../../mainTabsSlice";

const PathSubTabResponses = ({ pathName, methodName }: any) => {
  const paths = useAppSelector((state) => state.main.paths);
  const responses = paths[pathName][methodName].responses;
  const dispatch = useAppDispatch();
  const [showResponses, setShowResponses] = useState(true); // Initial visibility state

  console.log(responses);

  const toggleResponses = () => {
    setShowResponses((prevShowResponses) => !prevShowResponses);
  };

  return (
    <div className="mt-6">
      <div>
        {Object.entries(responses || {}).map(([key, value]) => {
          const responseDetails = value;
          return (
            <div key={key}>
              <InputWithMovingLabel
                inputProps={{
                  label: "Media-Type",
                  name: "response.statusCode",
                  value: key ?? "",
                  onChange: (e) => console.log(e),
                  type: "text",
                }}
                top
                className="mb-4"
              />
              <InputWithMovingLabel
                inputProps={{
                  label: "Media-Type",
                  name: "response.statusCode",
                  value: key ?? "",
                  onChange: (e) => console.log(e),
                  type: "text",
                }}
                top
                className="mb-4"
              />
            </div>
          );
        })}
        <button
          onClick={() => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "responseBody",
                data: {
                  ...paths[pathName][methodName].responseBody,
                  content: {
                    "change/me": {
                      schema: {},
                    },
                  },
                },
              })
            );
            toggleResponses(); // Togglling the visibility state
          }}
        >
          {showResponses && <Responses />}
        </button>
      </div>
    </div>
  );
};

export default PathSubTabResponses;
