import AddSecurityScheme from "./AddSecurityScheme";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { useEffect } from "react";
import { AddIcon, SaveIcon } from "../../assets/icons";
import styles from './addsecurityscheme.module.scss'
import { securityData } from "../../store/reducers/securitySlice";
import { Button } from "intelli-ui-components-library";
import { exportData } from "../../store/reducers/exportSlice";

const Security = () =>{
    const dispatch = useAppDispatch();
    const newData = useAppSelector(state => state.export);
    const { securitySchemes } = newData.components
    const newSecuritySchemes = useAppSelector(state => state.security.securitySchemes);
    const lengthObj = Object.keys(newSecuritySchemes).length
    const newObj = {...newData}
    const newComp = {...newObj.components}
    useEffect(()=>{
        const noData = {}
        securitySchemes? dispatch(securityData({...securitySchemes})) : dispatch(securityData(noData))
    },[])
   console.log(newSecuritySchemes)
    const addNewObj = () => {
         const newObj = {
            "api_key": {
                "type": "apiKey",
                "name": "api_key",
                "in": "header"
              }
         }
         const oldObjs = {...newSecuritySchemes}
         let newKey = true
         Object.keys(oldObjs).map((key:string)=>{
            if(key==="api_key")
               newKey= false
          })
          if(newKey)
          {
            const addObjs = {...oldObjs, ...newObj}
            dispatch(securityData({...addObjs}))
          }
    }
    const saveUpdatedData = () => {
        newComp["securitySchemes"] = newSecuritySchemes
        newObj["components"] = newComp
        dispatch(exportData({...newObj}))
    }
    return (
        <div>
            <div className={styles["security-addicon"]}><button className={styles["add-icon"]} onClick={addNewObj}><AddIcon fill="#FFFFFF" width="16px" height="16px"/></button></div>
        <div>
            {
                Object.keys(newSecuritySchemes).map((addScheme:any, index:number)=>{
                    return <AddSecurityScheme schemeKey={addScheme} index={index}/>
                })
            }    
        </div>
        <div className={styles["security-addicon"]}>
         {lengthObj>0 && <Button onClick={saveUpdatedData} className={styles["button"]}> <span className={styles["button-icon"]}>
              <SaveIcon />
            </span>Save</Button>}
        </div>
        
        </div>
        
    )
}
export default Security;
