import React, { useEffect, useState } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { toastFile } from "../../store/reducers/showToastSlice";
import styles from "./apiValidator.module.scss";
import { Button, FileUpload } from "intelli-ui-components-library";
import { DeleteIcon, PawIcon, SaveIcon, UploadIcon } from "../../assets/icons";
import { LoadingOutlined, SettingOutlined } from "@ant-design/icons";
import Modals from "../modals/Modals";
import { uploadFile } from "../../store/reducers/uploadSlice";
import { exportData } from "../../store/reducers/exportSlice";
import { updateApiValidatorState } from "../../store/reducers/apiValidatorSlice";

const BASE_URL = "http://localhost:8080";

const GeneratePostmanCollection = ({
  modalData,
  onCloseModal,
  onChangeModalData,
}: any) => {
  const dispatch = useAppDispatch();
  const uploadData = useAppSelector((state) => state.upload);
  const apiValidator = useAppSelector((state) => state.apiValidator);

  const {
    isCreatingCollection,
    createdCollectionData,
    hasDownloadedCollection,
  } = apiValidator;

  const [input, setInput] = useState<any>(uploadData);
  const [invalid, setInvalid] = useState(true);

  useEffect(() => {
    try {
      JSON.parse(input);
      setInvalid(false);
      dispatch(
        toastFile({
          message: "JSON Successfully Validated!",
          bgColor: "info",
          isVisible: true,
        })
      );
    } catch (e) {
      setInvalid(true);
      dispatch(
        toastFile({
          message: "Please Upload/Paste Valid JSON File",
          bgColor: "error",
          isVisible: true,
        })
      );
    }
  }, [input]);

  useEffect(() => {
    setInput(JSON.stringify(uploadData, null, 2));
  }, [uploadData]);

  const generatePostmanCollection = async () => {
    const formData = new FormData();
    const blob = new Blob([JSON.stringify(uploadData, null, 2)], {
      type: "application/json",
    });
    const file = new File([blob], "data.json", { type: "application/json" });
    formData.append("apiSpec", file);
    dispatch(updateApiValidatorState({ isCreatingCollection: true }));
    try {
      let response = await fetch(`${BASE_URL}/createPostmanCollection`, {
        method: "POST",
        body: formData,
      });
      const data = await response.json();

      const dataStr =
        "data:text/json;charset=utf-8," +
        encodeURIComponent(JSON.stringify(data));

      dispatch(updateApiValidatorState({ createdCollectionData: dataStr }));
      dispatch(updateApiValidatorState({ hasCreatedCollection: true }));
      dispatch(
        toastFile({
          message: "Collection generated successfully! Please download.",
          bgColor: "info",
          isVisible: true,
        })
      );
    } catch (error) {
      console.log(error);
    }
    dispatch(updateApiValidatorState({ isCreatingCollection: false }));
    dispatch(updateApiValidatorState({ hasDownloadedCollection: false }));
  };

  const downloadCollectionData = () => {
    if (!createdCollectionData) {
      dispatch(
        toastFile({
          message: "Please generate a postman collection.",
          bgColor: "warning",
          isVisible: true,
        })
      );
      return;
    }
    const downloadAnchorNode = document.createElement("a");
    downloadAnchorNode.setAttribute("href", createdCollectionData);
    downloadAnchorNode.setAttribute("download", "collection.json");
    document.body.appendChild(downloadAnchorNode);
    downloadAnchorNode.click();
    downloadAnchorNode.remove();
    dispatch(
      toastFile({
        message: "Collection downloaded successfully!",
        bgColor: "info",
        isVisible: true,
      })
    );
    dispatch(updateApiValidatorState({ hasDownloadedCollection: true }));
  };

  const handleOnRemoveAll = () => {
    onChangeModalData({
      title: "Are you sure?",
      description:
        "This action will remove properties from the document. Undo will be available.",
      onConfirm: () => {
        setInput("");
        onCloseModal();
      },
      showModal: true,
    });
  };

  const handleFileSelect = (file: File) => {
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const jsonData = JSON.parse(e.target.result);
          dispatch(uploadFile({ ...jsonData }));
          dispatch(exportData({ ...jsonData }));
        } catch {
          console.log("Something Wents Worng while reading the file");
        }
      };
      reader.readAsText(file);
    }
  };

  return (
    <div className={styles.container}>
      <div className={`${styles["textarea-container"]}`}>
        <textarea
          onChange={(e) => setInput(e.target.value)}
          value={input}
          className={`${styles["text-area"]}`}
          name="Open Api Json"
          rows={20}
        />
        <div className={`${styles["button-group"]}`}>
          <Button
            round="round"
            color="success"
            className={
              hasDownloadedCollection === false
                ? `${styles["button1"]} ${styles["bg-green"]}`
                : styles["button1"]
            }
            disabled={invalid}
            onClick={downloadCollectionData}
          >
            <span className={styles["button-icon"]}>
              <SaveIcon fill="#FFFFFF" />
            </span>
            {hasDownloadedCollection === false ? `Download (1)` : `Download`}
          </Button>
          <Button
            round="round"
            className={styles["button1"]}
            onClick={generatePostmanCollection}
          >
            <span className={styles["button-icon"]}>
              {isCreatingCollection ? (
                <LoadingOutlined />
              ) : (
                <SettingOutlined width="16px" height="16px" />
              )}
            </span>
            {isCreatingCollection ? `Generating....` : "Generate"}
          </Button>
          <Button
            round="round"
            className={styles["button"]}
            disabled={!input}
            onClick={handleOnRemoveAll}
          >
            <span className={styles["button-icon"]}>
              <DeleteIcon fill="#FFFFFF" width="16px" height="16px" />
            </span>
            REMOVE ALL
          </Button>
          {modalData.showModal && (
            <Modals
              showModal={modalData.showModal}
              header={modalData.title}
              description={modalData.description}
              onConfirm={modalData.onConfirm}
              onCancel={onCloseModal}
              childClassName=""
            />
          )}
          <FileUpload
            fileInputClass={styles.fileUpload}
            onFileSelect={handleFileSelect}
            fileType=".json"
          />
        </div>
      </div>
    </div>
  );
};

export default GeneratePostmanCollection;
