import React, { ChangeEvent, useState } from "react";

import {
  Button,
  DropDown,
  ToggleBtnGroup,
} from "intelli-ui-components-library";
import style from "./server.module.scss";

function TabList() {
  const [activeTab, setActiveTab] = useState("tab1");

  const handleTab1 = () => {
    // update the state to tab1
    setActiveTab("tab1");
  };

  const handleTab2 = () => {
    // update the state to tab2
    setActiveTab("tab2");
  };

  const handleTab3 = () => {
    // update the state to tab1
    setActiveTab("tab3");
  };

  const [values, setValues] = useState({});
  const [errors, setErrors] = useState({});

  const [value, setValue] = useState();

  const [selectedTab, setSelectedTab] = useState("");
  const clickHandle = (e: any, val: any) => {
    setSelectedTab(val);
  };

  const handleSubmit = (arrayOfOperations: any) => {
    const tempValues = { ...values };
    for (const val in tempValues) {
      console.log(tempValues);
    }
    console.log(value);
    console.log(arrayOfOperations);
  };

  return (
    <div className={style["tablist-section"]}>
      <ToggleBtnGroup
        onToggle={clickHandle}
        selectedTab={selectedTab}
        shouldSelectByDefault={true}
        borderBottom={true}
        disabled
        className={style["toggle-btn"]}
        styleObj={{ bgColor: "#0086ff", color: "#0086ff" }}
      >
        <ToggleBtnGroup.Btn
          value="tab1"
          className={activeTab === "tab1" ? "active" : ""}
          onClick={(e) => {
            console.log("......", e.target.value);
          }}
        >
          <div onClick={handleTab1}>URL</div>
        </ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn
          value="tab2"
          className={activeTab === "tab2" ? "active" : ""}
        >
          <div onClick={handleTab2}>Description</div>
        </ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn
          value="tab3"
          className={activeTab === "tab3" ? "active" : ""}
        >
          <div onClick={handleTab3}>Docs</div>
        </ToggleBtnGroup.Btn>
      </ToggleBtnGroup>

      <div className={style["tab-content"]}>
        {activeTab === "tab1" ? (
          <Url />
        ) : activeTab === "tab2" ? (
          <Description />
        ) : activeTab === "tab3" ? (
          <Enums />
        ) : (
          ""
        )}
      </div>
      <Button className={style["btnAlignment"]} onClick={handleSubmit}>
        &#x2719; Add Operation
      </Button>
    </div>
  );
}

const Url = () => {
  return <div>Url</div>;
};
const Description = () => {
  return <div>Description</div>;
};

const Enums = () => {
  return <div>Enum List</div>;
};

export default TabList;
