import { InputWithMovingLabel } from "intelli-ui-components-library";
import styles from './type.module.scss';
import { useAppDispatch } from "../../../store/hooks";
import { securityData } from "../../../store/reducers/securitySlice";
interface OpenidTypeProps {
    securityObj:any
    schemeKey:string
}

const OpenidType:React.FC<OpenidTypeProps> = ({securityObj, schemeKey}) =>{
    const newSecurityObj = {...securityObj}
    const addSchemeObj = {...newSecurityObj[schemeKey]}
    const dispatch = useAppDispatch();
    const onIdChange = (e:any) => {
        const newAddSchemeObj = {"type": addSchemeObj.type}
        newAddSchemeObj[e.target.name] = e.target.value
        newSecurityObj[schemeKey] = newAddSchemeObj
        dispatch(securityData({...newSecurityObj}))

    }
    return (
      <div className={styles["type-inputbox"]}>
        <InputWithMovingLabel
          inputProps={{
            type: "text",
            value: addSchemeObj.openIdConnectUrl,
            name: "openIdConnectUrl",
            label: "openIdConnect URL",
            onChange: onIdChange,
            className: styles["type-input"],
          }}
          top={true}
        />
      </div>
    );
}
export default OpenidType;
