import { InputWithMovingLabel } from "intelli-ui-components-library";
import styles from '../addsecurityscheme.module.scss'
import { useState } from "react";
import { useAppDispatch } from "../../../store/hooks";
import { securityData } from "../../../store/reducers/securitySlice";
import { CopyIcon, DeleteIcon } from "../../../assets/icons";

interface AddScopeProps {
    securityObj?:any,
    schemeKey?:string,
    scopeKey?:string,
    flowKey?:string,
    index?:number
}
const AddScope:React.FC<AddScopeProps> = ({securityObj, schemeKey ,flowKey, scopeKey, index}) => {
    const newSecurityObj = {...securityObj}
    const addSchemeObj = {...newSecurityObj[schemeKey]}
    const dispatch = useAppDispatch();
    const flowObj = {...addSchemeObj.flows};
    const descriptionObj = {...flowObj[flowKey]}
    const scopeObj = {...descriptionObj.scopes}
    const oldScope = scopeObj[scopeKey]
    const onScopeNameChange = (e:any, key:boolean) => {
        if(key){
        const newKey = e.target.value
        const keyValues = Object.entries(scopeObj);     
        keyValues.splice(index,1, [newKey,oldScope]);
        const newObj = Object.fromEntries(keyValues)
        descriptionObj['scopes'] = newObj
        }
        else{
            scopeObj[e.target.name] = e.target.value
            descriptionObj['scopes'] = scopeObj
        }
        flowObj[flowKey] = descriptionObj
        addSchemeObj['flows'] = flowObj
        newSecurityObj[schemeKey] = addSchemeObj
        dispatch(securityData({...newSecurityObj}))
    } 
    const deleteScope = () => {
        const keyValues = Object.entries(scopeObj);     
        keyValues.splice(index,1);
        const newObj = Object.fromEntries(keyValues)
        descriptionObj['scopes'] = newObj
        flowObj[flowKey] = descriptionObj
        addSchemeObj['flows'] = flowObj
        newSecurityObj[schemeKey] = addSchemeObj
        dispatch(securityData({...newSecurityObj}))
      
    }
    return (
        <div className={styles["add-scope"]}>
            <InputWithMovingLabel
                inputProps={{
                    type: "text",
                    value: scopeKey,
                    name:scopeKey,
                    label: "Scope Name",
                    onChange: (e)=>onScopeNameChange(e,true),
                    className: styles["header-input"]
                }}
                top={true}

            />
            <InputWithMovingLabel
                inputProps={{
                    type: "text",
                    value: scopeObj[scopeKey],
                    name: scopeKey,
                    label: "Description",
                    onChange:(e)=>onScopeNameChange(e,false),
                    className: styles["header-input"]
                }}
                top={true}

            />
            <div className={styles["security-copyicon"]} >
                {/* <span className={styles["copy-icon"]}><CopyIcon fill="#FFFFFF" width="20px" height="20px"/></span> */}
                <button className={styles["delete-icon"]} onClick={deleteScope}><DeleteIcon fill="#FFFFFF" width="20px" height="20px"/></button>
                </div>
        </div>
    )
}
export default AddScope;
