import { Button, InputWithMovingLabel, Tooltip } from "intelli-ui-components-library";
import { useState } from "react";
import styles from "../addsecurityscheme.module.scss";
import AddScope from "./AddScope";
import { useAppDispatch } from "../../../store/hooks";
import { securityData } from "../../../store/reducers/securitySlice";
import { AddIcon, EditIcon } from "../../../assets/icons";

interface TypeDescriptionProps {
  securityObj: any;
  schemeKey: string;
  flowKey: string;
}
const TypeDescription: React.FC<TypeDescriptionProps> = ({
  securityObj,
  schemeKey,
  flowKey,
}) => {
  const newSecurityObj = { ...securityObj };
  const addSchemeObj = { ...newSecurityObj[schemeKey] };
  const dispatch = useAppDispatch();
  const flowObj = { ...addSchemeObj.flows };
  const descriptionObj = { ...flowObj[flowKey] };
  const onAuthUrlChange = (e: any) => {
    descriptionObj[e.target.name] = e.target.value;
    flowObj[flowKey] = descriptionObj;
    addSchemeObj["flows"] = flowObj;
    newSecurityObj[schemeKey] = addSchemeObj;
    dispatch(securityData({ ...newSecurityObj }));
  };
  const addNewScope = () => {
    const newObj = {
      newScope: "description",
    };
    const oldObjs = { ...descriptionObj.scopes };
    let newKey = true;
    Object.keys(oldObjs).map((key: string) => {
      if (key === "newScope") newKey = false;
    });
    if (newKey) {
      const addObjs = { ...oldObjs, ...newObj };
      descriptionObj.scopes = addObjs;
      flowObj[flowKey] = descriptionObj;
      addSchemeObj["flows"] = flowObj;
      newSecurityObj[schemeKey] = addSchemeObj;
      dispatch(securityData({ ...newSecurityObj }));
    }
  };
  const { authorizationUrl, tokenUrl, refreshUrl } = descriptionObj;
  return (
    <>
      {authorizationUrl || tokenUrl || refreshUrl ? (
        <div className={styles["description-container"]}>
          <div className={styles["description-header"]}>Flow: {flowKey}</div>
          <div className={styles["type-description"]}>
            <InputWithMovingLabel
              inputProps={{
                type: "text",
                value: authorizationUrl,
                name: "authorizationUrl",
                label: "Authorization URL",
                onChange: onAuthUrlChange,
                className: styles["header-input"],
              }}
              top={true}
            />
            <InputWithMovingLabel
              inputProps={{
                type: "text",
                value: tokenUrl,
                name: "tokenUrl",
                label: "Token URL",
                onChange: onAuthUrlChange,
                className: styles["header-input"],
              }}
              top={true}
            />

            <InputWithMovingLabel
              inputProps={{
                type: "text",
                value: refreshUrl,
                name: "refreshUrl",
                label: "Refresh URL",
                onChange: onAuthUrlChange,
                className: styles["header-input"],
              }}
              top={true}
            />
          </div>
          <>
            <div className={styles["security-addicon"]}>
              <Tooltip message="Add Scope" position="top">
                <button className={styles["add-icon"]} onClick={addNewScope}>
                  <AddIcon fill="#FFFFFF" width="16px" height="16px" />
                </button>
              </Tooltip>
            </div>
            <div className={styles["scope-container"]}>
              {descriptionObj.scopes ? (
                Object.keys(descriptionObj.scopes).map(
                  (scope: string, index: number) => {
                    return (
                      <AddScope
                        securityObj={securityObj}
                        schemeKey={schemeKey}
                        flowKey={flowKey}
                        scopeKey={scope}
                        index={index}
                      />
                    );
                  }
                )
              ) : (
                <AddScope />
              )}
            </div>
          </>
        </div>
      ) : (
        ""
      )}
    </>
  );
};
export default TypeDescription;
