  Modal,
} from "intelli-ui-components-library";
//@ts-ignore
import styles from "./main.module.scss";
import { DeleteIcon, SaveIcon, CopyIcon, AddIcon } from "../../assets/icons";
import EditItem from "./EditItem";
import {
  convertToCustomStructure,
  convertToOriginalFormat,
} from "../../utils/conertData";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { updateData } from "../../store/reducers/exportSlice";
import requestTypes from "../../utils/constant/requestTypes";
import DemoJson from "../../utils/constant/demo.json";
import {
  VALID_METHODS,
  addMethod,
  addPath,
  copyMethod,
  deleteMethod,
} from "../../store/reducers/mainTabsSlice";

const EditMode = ({ Key, onHidden }: any) => {
  const [data, setData] = useState<[]>([]);

  const [path, setPath] = useState(Key);

  const reduxData = useAppSelector((state) => state.export.paths);
  const paths = useAppSelector((state) => state.main.paths);

  const [expanded, setIsExpanded] = useState("");
  const dispatch = useAppDispatch();

  const handleExpand = (key: any) => () => {
    setIsExpanded((pre) => (pre === key ? "" : key));
  };

  const handleDelete = (key: any) => (e: any) => {
    e.stopPropagation();
    // const deteItem = JSON.parse(JSON.stringify(reduxData));
    // if (data.length < 2) delete deteItem[Key];
    // //@ts-ignore
    // else delete deteItem[Key][key.key];
    // console.log(deteItem, reduxData);
    // dispatch(updateData({ key: "paths", data: deteItem }));
    // if (data.length < 2) onHidden();
    //@ts-ignore
    setData((p) => p.filter((x) => x.key !== key.key));
  };

  const handleCopy = (x: any) => (e: any) => {
    e.stopPropagation();
    const key: any = requestTypes.find((x) => {
      return !data.find((choice: any) => choice.key === x);
    });
    if (key) {
      //@ts-ignore
      setData((p) => [...p, { ...x, key }]);
    }
  };

  const handlePathChange = (event: any) => {
    const { name, value } = event.target;
    setPath(value);
  };

  const handleSave = () => {
    if (!Key) {
      dispatch({
        key: "paths",
        data: { ...paths, [path]: { get: { ...intitialStatePathMethod } } },
      });
    }
    dispatch(updateData({ key: "paths", data: paths }));
    onHidden();
  };

  const handleAdd = (e: any) => {
    e.stopPropagation();
    const key: any = requestTypes.find((x) => {
      return !data.find((choice: any) => choice.key === x);
    });
    if (key) {
      //@ts-ignore
      setData((p) => [...p, { key, data: {} }]);
    }
  };
  const handleParamAddEnum = (
    addEnum: any,
    val: any,
    enumCount: any,
    data: any
  ) => {
    addEnum(enumCount + 1);

    setData((p: any) => {
      let y = p.map((y: any) =>
        y.key === data.key
          ? {
              ...y,
              data: {
                ...y?.data,
                ["parameters"]: y?.data?.["parameters"].map(
                  (p: any, ele: any) =>
                    ele === val
                      ? {
                          ...p,
                          enumStatus: !!p?.enumStatus ? p?.enumStatus : true,
                          enumVal: enumCount + 1,
                          enumValObj:
                            p?.enumValObj?.length > 0 ? [...p?.enumValObj] : [],
                        }
                      : { ...p }
                ),
              },
            }
          : y
      );
      // console.log("handleParamAddEnum-y", y)

      let x = y.map((x: any) =>
        x.key === data.key
          ? {
              ...x,
              data: {
                ...x?.data,
                ["parameters"]: x?.data?.["parameters"].map(
                  (padd: any, pele: any) =>
                    pele === val
                      ? {
                          ...padd,
                          enumValObj: [...padd?.enumValObj, { addenumVal: "" }],
                        }
                      : { ...padd }
                ),
              },
            }
          : x
      );

      //  console.log("handleParamAddEnum-x", x)
      return x;
    });
  };
  const handleAddParam = (data: any, value: any, handleAdd: any) => {
    handleAdd(value + 1);
    setData((p: any) => {
      let y = p.map((y: any) =>
        y.key === data.key
          ? {
              ...y,
              data: {
                ...y?.data,
                ["parameters"]: [
                  ...y?.data?.["parameters"],
                  y?.data?.["parameters"][value],
                ],
              },
            }
          : y
      );
      // console.log("handleAddParam-y", y)
      let x = y.map((x: any) =>
        x.key === data.key
          ? {
              ...x,
              data: {
                ...x?.data,
                ["parameters"]: x?.data?.["parameters"].map(
                  (p: any, val: any) =>
                    val === x?.data?.["parameters"]?.length - 1
                      ? {
                          ...p,
                          enumStatus: false,
                          enumValObj: [],
                        }
                      : { ...p }
                ),
              },
            }
          : { ...x }
      );
      // console.log("handleAddParam-x", x)
      return x;
    });
  };

  const handleDeleteParam = (data: any, value: any) => {
    setData((p: any) => {
      const x = p.map((x: any) =>
        x.key === data.key && value > 0
          ? {
              ...x,
              data: {
                ...x?.data,
                ["parameters"]: x?.data?.["parameters"].filter(
                  (p: any, i: number) => i !== value
                ),
              },
            }
          : x
      );
      return x;
    });
  };

  const handleDeleteIcon = (
    addEnum: any,
    value: any,
    enumCount: any,
    data: any
  ) => {
    setData((p: any) => {
      const x = p.map((x: any) =>
        x.key === data.key
          ? {
              ...x,
              data: {
                ...x?.data,
                ["parameters"]: x?.data?.["parameters"]?.map(
                  (p: any, i: number) => ({
                    ...p,
                    enumValObj: p?.enumValObj?.filter(
                      (d: any, val: number) => val !== value
                    ),
                  })
                ),
              },
            }
          : x
      );
      return x;
    });
  };

  const handleAddEnumInputChange =
    (addEnum: any, addvalue: any, enumCount: any, data: any) =>
    (event: any) => {
      console.log("handleAddEnumInputChange-data", data);
      console.log("handleAddEnumInputChange-addvalue", addvalue);

      let { name, value } = event.target;
      console.log("handleAddEnumInputChange-addname", name);
      console.log("handleAddEnumInputChange-addvalue", value);
      setData((p: any) => {
        const x = p.map((x: any) =>
          x.key === data.key
            ? {
                ...x,
                data: {
                  ...x?.data,
                  ["parameters"]: x?.data?.["parameters"]?.map(
                    (p: any, i: number) => ({
                      ...p,
                      enumValObj: p?.enumValObj?.map((d: any, val: number) =>
                        val === addvalue ? { addenumVal: value } : { ...d }
                      ),
                    })
                  ),
                },
              }
            : x
        );
        return x;
      });
    };

  const handleitemInputChange = (d: any, i: any) => (event: any) => {
    let { name, value } = event.target;
    const [parentKey, childKey] = name.split(",")[0].split(".");
    //@ts-ignore
    if (name.split(",")[0] !== "parameters") {
      setData((p: any) => {
        const x = p.map((x: any) =>
          x.key === d.key
            ? {
                ...x,
                data: {
                  ...x.data,
                  [parentKey]: childKey
                    ? { ...x.data?.[parentKey], [childKey]: value }
                    : value,
                },
              }
            : x
        );
        return x;
      });
    } else if (
      name.split(",")[0] === "parameters" &&
      name.split(",")[1] === "format"
    ) {
      setData((p: any) => {
        const x = p.map((x: any, id: any) =>
          x.key === d.key
            ? {
                ...x,
                data: {
                  ...x.data,
                  [parentKey]: x?.data?.[parentKey].map((p: any, val: any) =>
                    val === i
                      ? {
                          ...p,
                          ["schema"]: {
                            ...p["schema"],
                            [name.split(",")[1]]: value,
                          },
                        }
                      : { ...p }
                  ),
                },
              }
            : x
        );
        return x;
      });
    } else {
      setData((p: any) => {
        const x = p.map((x: any, id: any) =>
          x.key === d.key
            ? {
                ...x,
                data: {
                  ...x.data,
                  [parentKey]: x?.data?.[parentKey].map((p: any, val: any) =>
                    val === i
                      ? {
                          ...p,
                          [name.split(",")[1]]: value,
                        }
                      : {
                          ...p,
                        }
                  ),
                },
              }
            : x
        );
        return x;
      });
    }
  };

  const handleMethodChange =
    (d: any, i: any) =>
    ({ key, prev, name }: any) => {
      if (name[0] !== "parameters") {
        setData((p: any) =>
          p.map((x: any) => (x.key === prev ? { ...x, key } : x))
        );
      } else if (name[0] === "parameters" && name[1] === "type") {
        setData((p: any) => {
          const x = p.map((x: any) =>
            x.key === prev
              ? {
                  ...x,
                  data: {
                    ...x.data,
                    [name[0]]: x?.data?.[name[0]]?.map((p: any, val: any) =>
                      val === i
                        ? {
                            ...p,
                            ["schema"]: { ...p["schema"], [name[1]]: key },
                          }
                        : { ...p }
                    ),
                  },
                }
              : x
          );
          return x;
        });
      } else if (name[0] === "parameters" && name[1] === "in") {
        setData((p: any) => {
          const x = p.map((x: any) =>
            x.key === prev
              ? {
                  ...x,
                  data: {
                    ...x.data,
                    [name[0]]: x?.data?.[name[0]]?.map((p: any, val: any) =>
                      val === i
                        ? {
                            ...p,
                            [name[1]]: key,
                          }
                        : { ...p }
                    ),
                  },
                }
              : x
          );
          return x;
        });
      }
    };

  const handleitemCheckChange = (d: any, name: any) => (event: any) => {
    let { checked } = event.target;
    // Split the name to access nested properties
    const [parentKey, childKey] = name.split(".");
    //@ts-ignore
    setData((p) => {
      const x = p.map((x: any) =>
        x.key === d.key
          ? {
              ...x,
              data: {
                ...x.data,
                [parentKey]: childKey
                  ? { ...x.data?.[parentKey], [childKey]: checked }
                  : checked,
              },
            }
          : x
      );
      return x;
    });
  };
  const handleAddData = (value: any) => {
    const { name, key, data } = value;
    const [parentKey, childKey] = name.split(".");
    //@ts-ignore
    setData((p) => {
      const x = p.map((x: any) =>
        x.key === key
          ? {
              ...x,
              data: {
                ...x.data,
                [parentKey]: childKey
                  ? {
                      ...x.data?.[parentKey],
                      [childKey]: data,
                    }
                  : data,
              },
            }
          : x
      );
      return x;
    });
    console.log(data, value);
  };

  const handleRequiredChange = (data: any, name: any) => {
    setData((p: any) => {
      const x = p.map((x: any) => ({
        ...x,
        data: {
          ...x.data,
          [name[0]]: x?.data?.[name[0]]?.map((p: any, i: any) =>
            val === i
              ? {
                  ...p,
                  [name[1]]: !data,
                }
              : { ...p }
          ),
        },
      }));
      return x;
    });
  };
  const handleInputIncrementChange = (data: any) => {
    // console.log("data", data)
    // console.log("data", name)
    setData((p: any) => {
      const x = p.map((x: any) => ({
        ...x,
        data: {
          ...x.data,
          [data?.target?.name.split(",")[0]]: x?.data?.[
            data?.target?.name.split(",")[0]
          ]?.map((p: any, val: any) =>
            val === Number(data?.target?.name.split(",")[2])
              ? {
                  ...p,
                  [data.target.name.split(",")[1]]: Number(data.target.value),
                }
              : { ...p }
          ),
        },
      }));
      return x;
    });
  };

  useEffect(() => {
    //@ts-ignore
    const formatData = convertToCustomStructure(reduxData[Key], 1);
    // console.log({ formatData, data });
    setData(formatData);
  }, [reduxData]);

  return (
    <Modal
      onHidden={onHidden}
      showOverlay={true}
      setting={{
        modalId: "center-btn",
        className: styles["modal"],
        variant: "action",
      }}
    >
      <Modal.CloseIcon />
      <Modal.Header>{Key ? "Edit Operation" : "Add Path"}</Modal.Header>
      <InputWithMovingLabel
        inputProps={{
          label: "Method",
          name: "title",
          value: path,
          onChange: handlePathChange,
          type: "text",
          className: "!text-black",
        }}
        top
        className="mb-2"
      />
      <Modal.Body>
        <div
          style={{ display: "flex", flexDirection: "column", width: "100%" }}
        >
          {Key ? (
            <>
              <button
                title="Add Method"
                onClick={() => {
                  let selectedPath = paths[path];
                  if (!paths[path]) {
                    dispatch(addMethod({ pathName: path, methodName: "get" }));
                  } else {
                    const allowedMethods = VALID_METHODS.filter(
                      (method) => !Object.keys(selectedPath).includes(method)
                    );
                    if (allowedMethods.length === 0) {
                      return;
                    } else {
                      dispatch(
                        addMethod({
                          pathName: path,
                          methodName: allowedMethods[0],
                        })
                      );
                    }
                  }
                }}
                disabled={requestTypes.length === data.length}
                style={{
                  width: "fit-content",
                  alignSelf: "flex-end",
                  margin: "16px 0px",
                  backgroundColor: "#000",
                  borderRadius: "4px",
                }}
              >
                <AddIcon fill="#fff" width="16px" height="16px" />
              </button>
              <div>
                {paths[path]
                  ? Object.keys(paths[path]).map((method: any) => (
                      <Accordion.Section
                        key={method}
                        className="w-full block bg-white"
                        header={
                          <div>
                            <span className="uppercase">{method}</span>
                            <span style={{ marginLeft: "16px" }}>
                              {paths[path][method].operationId}
                            </span>
                          </div>
                        }
                        expanded={expanded === method}
                        onOpenClick={handleExpand(method)}
                        rightChild={
                          <>
                            <Button
                              variant="text"
                              title="Copy"
                              className="mr-2"
                              onClick={(e) => {
                                e.stopPropagation();
                                let selectedPath = paths[path];
                                const allowedMethods = VALID_METHODS.filter(
                                  (method) =>
                                    !Object.keys(selectedPath).includes(method)
                                );

                                if (allowedMethods.length === 0) {
                                  return;
                                } else {
                                  dispatch(
                                    copyMethod({
                                      pathName: path,
                                      methodName: allowedMethods[0],
                                      data: paths[path][method],
                                    })
                                  );
                                }
                              }}
                            >
                              <CopyIcon />
                            </Button>
                            <Button
                              variant="text"
                              title="Delete"
                              onClick={(e) => {
                                e.stopPropagation();
                                dispatch(
                                  deleteMethod({
                                    pathName: path,
                                    methodName: method,
                                  })
                                );
                              }}
                            >
                              <DeleteIcon />
                            </Button>
                          </>
                        }
                      >
                        <EditItem pathName={path} methodName={method} />
                      </Accordion.Section>
                    ))
                  : ""}
              </div>
            </>
          ) : (
            <></>
          )}

          <Button
            onClick={() => {
              if (!path) {
                return;
              }
              handleSave();
            }}
            style={{
              width: "fit-content",
              alignSelf: "center",
              backgroundColor: "#9e9e9e",
              borderRadius: "4px",
            }}
          >
            <span className={styles["button-icon"]}>
              <SaveIcon />
            </span>
            Save
          </Button>
        </div>
      </Modal.Body>
    </Modal>
  );
};

export default EditMode;
