import React, { useState } from "react";
import styles from "./apiValidator.module.scss";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { toastFile } from "../../store/reducers/showToastSlice";
import { Button, FileUpload, Radio } from "intelli-ui-components-library";
import {
  CaretRightOutlined,
  EyeOutlined,
  LoadingOutlined,
} from "@ant-design/icons";
import { PawIcon, SaveIcon, UploadIcon } from "../../assets/icons";
import Modals from "../modals/Modals";
import { updateApiValidatorState } from "../../store/reducers/apiValidatorSlice";

const BASE_URL = "http://localhost:8080";

const RunPostmanCollection = ({ modalData, onCloseModal }: any) => {
  const dispatch = useAppDispatch();
  const apiValidator = useAppSelector((state) => state.apiValidator);
  const [isCreatingCollectionReport, setIsCreatingCollectionReport] =
    useState(false);

  const {
    hasCreatedCollection,
    fileSelectForReport,
    collectionDataForReport,
    isCreatingReport,
    reportDataAsHTMLString,
    hasViewedReport,
    hasDownloadedReport,
  } = apiValidator;

  const runPostmanCollection = async () => {
    if (!collectionDataForReport && fileSelectForReport === "uploaded") {
      dispatch(
        toastFile({
          message:
            "Please upload a postman collection or select the latest postman collection that was downloaded",
          bgColor: "warning",
          isVisible: true,
        })
      );
      return;
    }
    const formData = new FormData();
    const blob = new Blob([JSON.stringify(collectionDataForReport, null, 2)], {
      type: "application/json",
    });
    const file = new File([blob], "data.json", { type: "application/json" });
    formData.append("postmanCollection", file);

    dispatch(updateApiValidatorState({ isCreatingReport: true }));
    try {
      let response = await fetch(`${BASE_URL}/runPostmanCollection`, {
        method: "POST",
        body: formData,
      });

      const data = await response.text();
      dispatch(updateApiValidatorState({ reportDataAsHTMLString: data }));
    } catch (error) {
      console.log(error);
    }
    dispatch(updateApiValidatorState({ isCreatingReport: false }));
  };

  const viewHtmlReportInNewTab = () => {
    // Create a new Blob with the HTML content
    const blob = new Blob([reportDataAsHTMLString], { type: "text/html" });
    let url = window.URL.createObjectURL(blob);
    window.open(url, "_blank");
    window.URL.revokeObjectURL(url);
    dispatch(updateApiValidatorState({ hasViewedReport: true }));
  };

  const downloadReport = () => {
    const blob = new Blob([reportDataAsHTMLString], { type: "text/html" });
    let url = window.URL.createObjectURL(blob);

    let a = document.createElement("a");
    a.href = url;
    a.download = "report.html";
    a.click();
    window.URL.revokeObjectURL(url);
    dispatch(updateApiValidatorState({ hasDownloadedReport: true }));
  };

  const uplodadPostmanCollection = (file: File) => {
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const jsonData = JSON.parse(e.target.result);
          dispatch(
            updateApiValidatorState({ collectionDataForReport: jsonData })
          );
        } catch {
          console.log("Something Wents Worng while reading the file");
        }
      };

      reader.readAsText(file);
    }
  };
  return (
    <div className={styles.container}>
      <div className={`${styles["textarea-container"]}`}>
        <div className={styles.runpostman}>
          {hasCreatedCollection && (
            <div className={styles.runpostman}>
              <div className={styles.itemCenter}>
                <div
                  onClick={() =>
                    dispatch(
                      updateApiValidatorState({
                        fileSelectForReport: "generated",
                      })
                    )
                  }
                >
                  <Radio
                    label="Select Generated Postman Collection"
                    size="sm"
                    checked={fileSelectForReport === "generated"}
                  />
                </div>
                <p
                  style={{
                    marginLeft: "20px",
                    marginTop: "8px",
                    textDecoration: "underline",
                    color: "blue",
                  }}
                >
                  collection.json
                </p>
              </div>
              <p className={styles.itemDist}>Or</p>
            </div>
          )}
          <div className={styles.runpostman}>
            <div className={styles.itemCenter}>
              <div
                onClick={() =>
                  dispatch(
                    updateApiValidatorState({
                      fileSelectForReport: "uploaded",
                    })
                  )
                }
              >
                <Radio
                  label="Upload Postman Collection"
                  size="sm"
                  checked={fileSelectForReport === "uploaded"}
                />
              </div>

              <FileUpload
                fileInputClass={styles.fileUpload}
                onFileSelect={uplodadPostmanCollection}
                fileType=".json"
              />
            </div>
            <div className={`${styles["button-group1"]}`}>
              <Button
                round="round"
                className={styles["button1"]}
                onClick={runPostmanCollection}
              >
                <span className={styles["button-icon"]}>
                  {isCreatingCollectionReport ? (
                    <LoadingOutlined />
                  ) : (
                    <CaretRightOutlined />
                  )}
                </span>
                <span>{isCreatingCollectionReport ? "Running..." : "Run"}</span>
              </Button>
              <Button
                round="round"
                color="success"
                className={
                  reportDataAsHTMLString
                    ? `${styles["button1"]} ${styles["bg-green"]}`
                    : styles["button1"]
                }
                //disabled={invalid}
                onClick={viewHtmlReportInNewTab}
              >
                <span className={styles["button-icon"]}>
                  <EyeOutlined />
                </span>
                <span>
                  {reportDataAsHTMLString && hasViewedReport === false
                    ? `View Report (1)`
                    : "View Report"}
                </span>
              </Button>
              <Button
                round="round"
                color="success"
                className={
                  reportDataAsHTMLString
                    ? `${styles["button1"]} ${styles["bg-green"]}`
                    : styles["button1"]
                }
                onClick={downloadReport}
              >
                <span className={styles["button-icon"]}>
                  <SaveIcon fill="#FFFFFF" />
                </span>
                {hasDownloadedReport === false && reportDataAsHTMLString
                  ? `Download (1)`
                  : "Download Report"}
              </Button>
            </div>
            {modalData.showModal && (
              <Modals
                showModal={modalData.showModal}
                header={modalData.title}
                description={modalData.description}
                onConfirm={modalData.onConfirm}
                onCancel={onCloseModal}
                childClassName=""
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RunPostmanCollection;
