import { InputWithMovingLabel,DropDown } from "intelli-ui-components-library"
import styles from './type.module.scss';
import { useAppDispatch } from "../../../store/hooks";
import { securityData } from "../../../store/reducers/securitySlice";

interface APITypeProps {
securityObj:any
schemeKey:string
}

const APIType:React.FC<APITypeProps> = ({securityObj, schemeKey}) =>{
    const newSecurityObj = {...securityObj}
    const addSchemeObj = {...newSecurityObj[schemeKey]}
    const dispatch = useAppDispatch();
    const optionList = [
        { label: "Header", key: "header" },
        { label: "Query", key: "query" },
        { label: "Cookie", key: "cookie" },
    ];
    const onNameChange = (e:any) => {
        const newAddSchemeObj = {"type": addSchemeObj.type}
        newAddSchemeObj[e.target.name] = e.target.value
        newSecurityObj[schemeKey] = newAddSchemeObj
        dispatch(securityData({...newSecurityObj}))
    }
    const onLocationChange = (e:any) => {
        addSchemeObj.in = e.key
        newSecurityObj[schemeKey] = addSchemeObj
        dispatch(securityData({...newSecurityObj}))
    }
   return (
    <div className={styles["api-container"]}>
        <InputWithMovingLabel
                    inputProps={{
                        type: "text",
                        name: "name",
                        value: addSchemeObj.name,
                        label: "Parameter Name",
                        onChange: onNameChange,
                        className: styles["type-input"]
                    }}
                    top={true}
                />
                <DropDown
                    optionList={optionList}
                    value={addSchemeObj.in}
                    onChange={onLocationChange}
                    label="Location"
                />
    </div>
   )
}
export default APIType
