// @ts-nocheck
import {
  Checkbox,
  DropDown,
  InputWithMovingLabel,
  MultiAutoComplete,
  ToggleBtnGroup,
  AutoComplete,
  Button,
  Radio,
  Card,
} from "intelli-ui-components-library";
import React, { useEffect, useState } from "react";
import styles from "./maintab.module.scss";
import { convertToOriginalFormat } from "../../utils/conertData";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import AddEnum from "./AddEnum";
import CreateParam from "./subtabs/parameters/CreateParam";
import Responses from "./Responses";
import CreateCallback from "./CreateCallback";
import requestTypes, {
  locationTypes,
  dataTypes,
  formatTypes,
} from "../../utils/constant/requestTypes";
import { DeleteIcon, SaveIcon, CopyIcon, AddIcon } from "../../assets/icons";
import {
  DEFAULT_PARAM_OBJ,
  addMethod,
  updatePath,
} from "../../store/reducers/mainTabsSlice";
import { DEFAULT_Callback_OBJ } from "./mainTabsSlice";
import PathSubTabMain from "./subtabs/main/PathSubTabMain";
import PathSubTabDescription from "./subtabs/description/PathSubTabDescription";
import PathSubTabsDocs from "./subtabs/docs/PathSubTabDocs";
import PathSubTabRequest from "./subtabs/requestBody/PathSubTabRequest";

const EditPathSubTabs = ({ pathName, methodName }: any) => {
  const [selectedTab, setSelectedTab] = useState("main");

  const content = () => {
    switch (selectedTab) {
      case "main":
        return <PathSubTabMain pathName={pathName} methodName={methodName} />;
      case "desc":
        return (
          <PathSubTabDescription pathName={pathName} methodName={methodName} />
        );
      case "docs":
        return <PathSubTabsDocs pathName={pathName} methodName={methodName} />;
      case "parameters":
        return <Params pathName={pathName} methodName={methodName} />;
      case "req":
        return (
          <PathSubTabRequest pathName={pathName} methodName={methodName} />
        );
      case "sec":
        return <Sec pathName={pathName} methodName={methodName} />;
      case "call":
        return <Callbacks pathName={pathName} methodName={methodName} />;
      case "res":
        return <Res pathName={pathName} methodName={methodName} />;
      default:
        return <div>default</div>;
    }
  };
  return (
    <div>
      <ToggleBtnGroup
        onToggle={(e: { target: { value: React.SetStateAction<string> } }) =>
          setSelectedTab(e.target.value)
        }
        selectedTab={selectedTab}
        shouldSelectByDefault={true}
        borderBottom={true}
        disabled
        styleObj={{ bgColor: "#0086ff", color: "#0086ff" }}
      >
        <ToggleBtnGroup.Btn value="main">Main</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="desc">Description</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="docs">Docs</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="parameters">Parameters</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="req">Request Body</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="res">Responses</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="sec">Security</ToggleBtnGroup.Btn>
        {/* <ToggleBtnGroup.Btn value="link">Links</ToggleBtnGroup.Btn> */}
        <ToggleBtnGroup.Btn value="call">Callbacks</ToggleBtnGroup.Btn>
      </ToggleBtnGroup>
      <div>{content()}</div>
    </div>
  );
};

const Params = ({ pathName, methodName }: any) => {
  const paths = useAppSelector((state) => state.main.paths);
  const dispatch = useAppDispatch();
  const parametersArr = paths[pathName][methodName]?.parameters ?? [];

  return (
    <div className={styles.parameterContainer}>
      {parametersArr.length > 0 ? (
        parametersArr.map((params: any, i: number) => (
          <>
            <>
              <div className="text-left w-full">
                Parameter:
                <button
                  className="bg-gray-400 rounded-md p-1 disabled:bg-gray-200 hover:bg-gray-500"
                  title="Add"
                  onClick={() => {
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...paths[pathName][methodName].parameters,
                          DEFAULT_PARAM_OBJ,
                        ],
                      })
                    );
                  }}
                >
                  <AddIcon fill="#FFFFFF" width="16px" height="16px" />
                </button>
                <button
                  className="bg-gray-400 rounded-md p-1 disabled:bg-gray-200 hover:bg-gray-500"
                  title="Duplicate"
                  onClick={() => {}}
                >
                  <CopyIcon fill="#FFFFFF" width="16px" height="16px" />
                </button>
                <button
                  className="bg-gray-400 rounded-md p-1 disabled:bg-gray-200 hover:bg-gray-500"
                  title="Delete"
                  onClick={() => {
                    const filteredParamsArr = parametersArr.filter(
                      (el, index) => index !== i
                    );
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [...filteredParamsArr],
                      })
                    );
                  }}
                >
                  <DeleteIcon fill="#FFFFFF" width="16px" height="16px" />
                </button>
                <span>{i > 0 ? "newParam" : `${params?.name}`}</span>
              </div>
              <div className={styles.inputParamName}>
                <InputWithMovingLabel
                  inputProps={{
                    label: "Parameter Name",
                    name: ["parameters", "name"],
                    value: params?.name || "",
                    onChange: (event) => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        name: event.target.value,
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    },
                    type: "text",
                  }}
                  top={true}
                />
              </div>
              <div className={styles.inputDescription}>
                <InputWithMovingLabel
                  inputProps={{
                    label: "Parameter Description",
                    name: ["parameters", "description"],
                    value: params?.description || "",
                    onChange: (event) => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        description: event.target.value,
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    },
                    type: "text",
                  }}
                  top
                />
              </div>
              <div className={styles.twoItemGrid}>
                <DropDown
                  optionList={locationTypes.map((x) => ({
                    label: x,
                    key: x,
                    //prev: data?.key,
                    name: ["parameters", "in"],
                  }))}
                  label="Location"
                  value={params?.in || ""}
                  onChange={(selectedOption) => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      in: selectedOption.key,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  className={styles.dropdownLocation}
                />
                <DropDown
                  optionList={dataTypes.map((x) => ({
                    label: x,
                    key: x,
                    //prev: data?.key,
                    name: ["parameters", "type"],
                  }))}
                  label="Data Type"
                  value={params?.schema?.type || ""}
                  onChange={(selectedOption) => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      schema: {
                        ...parametersArr[i].schema,
                        type: selectedOption.key,
                      },
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  className={styles.dropdownType}
                />
              </div>
              <div className={styles.twoItemGrid}>
                <InputWithMovingLabel
                  inputProps={{
                    label: "Default Value",
                    name: ["parameters", "default"],
                    value: params?.default || "",
                    onChange: (event) => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        default: event.target.value,
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    },
                    type: "text",
                  }}
                  top={true}
                  className={styles.inputDefault}
                />
                <div className={styles.autoComplete}>
                  <DropDown
                    optionList={formatTypes.map((x) => ({
                      label: x,
                      key: x,
                      //prev: data?.key,
                      name: ["parameters", "type"],
                    }))}
                    label="Data Type"
                    value={params?.schema?.format || ""}
                    onChange={(selectedOption) => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        schema: {
                          ...parametersArr[i].schema,
                          format: selectedOption.key,
                        },
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    }}
                    className={styles.dropdownType}
                  />
                </div>
              </div>
              <div className={styles.threeItemGrid}>
                <Checkbox
                  value="Min.Exclusive"
                  color="info"
                  checked={params?.minRequired}
                  onChange={() => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      minRequired: !params.minRequired,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  size="sm"
                  className={styles.checkBox}
                />
                <Checkbox
                  value="Max. Exclusive"
                  color="info"
                  checked={params?.maxRequired}
                  onChange={() => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      maxRequired: !params.maxRequired,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  size="sm"
                  className={styles.checkBox}
                />
                <label htmlFor="minInput">Min Length </label>
                <input
                  id="minInput"
                  type="number"
                  name={["parameters", "minLen", i]}
                  value={params?.minLength || 0}
                  onChange={(event) => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      minLength: event?.target.value,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  min={0}
                  max={100}
                  step={1}
                />
                <label htmlFor="maxInput">Max Length </label>
                <input
                  id="maxInput"
                  type="number"
                  name={["parameters", "maxLen", i]}
                  value={params?.maxLength || 0}
                  onChange={(event) => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      maxLength: event?.target.value,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  min={0}
                  max={100}
                  step={1}
                />
              </div>

              <InputWithMovingLabel
                inputProps={{
                  label: "Pattern",
                  name: ["parameters", "pattern"],
                  value: params?.pattern || "",
                  onChange: (event) => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      pattern: event?.target.value,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  },
                  type: "text",
                }}
                top
                className="mb-4"
              />

              <div className={styles.threeItemGrid}>
                <div>
                  <Button
                    children="Add Enum"
                    size="md"
                    variant="contained"
                    round="round"
                    color="success"
                    onClick={() => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        schema: {
                          ...parametersArr[i].schema,
                          enum: [
                            ...parametersArr[i].schema.enum,
                            "newEnumValue",
                          ],
                        },
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    }}
                    className="mb-2"
                  />
                </div>
                <div>
                  <label htmlFor="maxInput">Multiple Of </label>
                  <input
                    id="multipleOf"
                    type="number"
                    name={["parameters", "multiOf", i]}
                    value={params?.multiOf || 0}
                    onChange={(event) => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        multiOf: event.target.value,
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    }}
                    min={0}
                    max={100}
                    step={1}
                  />
                </div>
                <div>
                  <Checkbox
                    value="Unique Items"
                    color="info"
                    color="info"
                    defaultChecked={params?.uniqueItems}
                    onChange={(event) => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        uniqueItems: !params.uniqueItems,
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    }}
                    size="sm"
                    className={styles.checkBox}
                  />
                </div>
              </div>

              <div>
                {params.schema.enum.length > 0 &&
                  params.schema.enum.map((enumValue, enumIndex) => (
                    <div className={styles["enum-array"]}>
                      <span
                        onClick={() => {
                          const updatedObjAtIndex = {
                            ...parametersArr[i],
                            schema: {
                              ...parametersArr[i].schema,
                              enum: [
                                ...parametersArr[i].schema.enum,
                                "newEnumValue",
                              ],
                            },
                          };
                          dispatch(
                            updatePath({
                              pathName,
                              methodName,
                              methodKey: "parameters",
                              data: [
                                ...parametersArr.slice(0, i),
                                updatedObjAtIndex,
                                ...parametersArr.slice(
                                  i + 1,
                                  parametersArr.length
                                ),
                              ],
                            })
                          );
                        }}
                        style={{
                          margin: "10px 10px 0px 0px",
                        }}
                      >
                        <AddIcon fill="black" />
                      </span>
                      <span
                        onClick={() => {
                          const filteredEnumArr = params.schema.enum.filter(
                            (el, idx) => idx !== enumIndex
                          );
                          const updatedObjAtIndex = {
                            ...parametersArr[i],
                            schema: {
                              ...parametersArr[i].schema,
                              enum: [...filteredEnumArr],
                            },
                          };
                          dispatch(
                            updatePath({
                              pathName,
                              methodName,
                              methodKey: "parameters",
                              data: [
                                ...parametersArr.slice(0, i),
                                updatedObjAtIndex,
                                ...parametersArr.slice(
                                  i + 1,
                                  parametersArr.length
                                ),
                              ],
                            })
                          );
                        }}
                        style={{
                          margin: "10px 10px 0px 0px",
                        }}
                      >
                        <DeleteIcon width="20" height="20" fill="black" />
                      </span>
                      <InputWithMovingLabel
                        key={enumIndex}
                        inputProps={{
                          label: "Enum Value",
                          name: ["parameters", "enum", enumIndex],
                          value: i > 0 ? "" : enumValue,
                          onChange: (event) => {
                            const enumArray = params.schema.enum;
                            const updatedEnumArr = [
                              ...enumArray.slice(0, enumIndex),
                              event.target.value,
                              ...enumArray.slice(
                                enumIndex + 1,
                                enumArray.length
                              ),
                            ];
                            const updatedObjAtIndex = {
                              ...parametersArr[i],
                              schema: {
                                ...parametersArr[i].schema,
                                enum: [...updatedEnumArr],
                              },
                            };
                            dispatch(
                              updatePath({
                                pathName,
                                methodName,
                                methodKey: "parameters",
                                data: [
                                  ...parametersArr.slice(0, i),
                                  updatedObjAtIndex,
                                  ...parametersArr.slice(
                                    i + 1,
                                    parametersArr.length
                                  ),
                                ],
                              })
                            );
                          },
                          type: "text",
                          className: "enum-input__inner",
                        }}
                        top
                        className="mb-2 enum-input__outer"
                      />
                    </div>
                  ))}
              </div>

              <hr className={styles.horLine}></hr>
            </>
          </>
        ))
      ) : (
        <CreateParam pathName={pathName} methodName={methodName} />
      )}
    </div>
  );
};

const Sec = ({ pathName, methodName }: any) => {
  const paths = useAppSelector((state) => state.main.paths);
  const securitySchemes = useAppSelector(
    (state) => state.security.securitySchemes
  );
  const dispatch = useAppDispatch();

  const [selectedValue, setSelectedValue] = useState("");

  useEffect(() => {
    if (Object.keys(securitySchemes).length > 0) {
      setSelectedValue("custom");
    }
  }, [securitySchemes]);

  useEffect(() => {
    let updatedSecurityData = [];
    if (selectedValue === "default") {
      updatedSecurityData = [];
    } else if (selectedValue === "disabled") {
      updatedSecurityData = [];
    } else if (selectedValue === "custom") {
      updatedSecurityData = Object.keys(securitySchemes).map((key) => ({
        [key]: "",
      }));
    } else {
      updatedSecurityData = [];
    }
    dispatch(
      updatePath({
        pathName,
        methodName,
        methodKey: "security",
        data: [...paths[pathName][methodName].security, ...updatedSecurityData],
      })
    );
  }, [selectedValue]);

  return (
    <Card className={styles["security-subtab"]}>
      <div className={styles["security-subtab__radio"]}>
        <div onClick={() => setSelectedValue("default")}>
          <Radio
            label={"Use default security"}
            checked={selectedValue === "default"}
          />
        </div>
        <div onClick={() => setSelectedValue("disabled")}>
          <Radio
            label={"Disable security"}
            checked={selectedValue === "disabled"}
          />
        </div>
        <div onClick={() => setSelectedValue("custom")}>
          <Radio
            label={"Use custom security"}
            checked={selectedValue === "custom"}
          />
        </div>
      </div>

      {selectedValue === "custom" &&
      paths[pathName][methodName].security.length > 0 ? (
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "flex-start",
          }}
        >
          <p>Selected Security Schemes</p>
          {paths[pathName][methodName].security.map((obj) => {
            let k = Object.keys(obj)[0];
            let v = obj[k];
            return <span key={k}>{`${k}`}</span>;
          })}
        </div>
      ) : (
        <></>
      )}
    </Card>
  );
};

