import {
  Button,
  FileUpload,
  Checkbox,
  Radio,
  ToggleBtnGroup,
} from "intelli-ui-components-library";
import { useEffect, useState } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { uploadFile } from "../../store/reducers/uploadSlice";
import styles from "./apiValidator.module.scss";
import { toastFile } from "../../store/reducers/showToastSlice";
import DemoJson from "../../utils/constant/demo.json";
import { DeleteIcon, UploadIcon, PawIcon } from "../../assets/icons";
import SnackBar from "../snackbar/SnackBar";
import Modals from "../modals/Modals";
import { exportData } from "../../store/reducers/exportSlice";
import { LoadingOutlined } from "@ant-design/icons";

const BASE_URL = "http://localhost:8080";

const ApiValidator = () => {
  const dispatch = useAppDispatch();
  const uploadData = useAppSelector((state) => state.upload);

  const [selectedTab, setSelectedTab] = useState("generate-postman");
  const [selectValue, setSelectvalue] = useState(false);
  const [modelData, setModelData] = useState({
    title: "",
    description: "",
    onConfirm: () => {},
    showModal: false,
  });

  const closeModal = () => {
    setModelData({
      title: "",
      description: "",
      onConfirm: () => {},
      showModal: false,
    });
  };

  return (
    <>
      <ToggleBtnGroup
        onToggle={(e: any, val: any) => setSelectedTab(val)}
        selectedTab={selectedTab}
        shouldSelectByDefault={true}
        borderBottom={false}
        disabled
        styleObj={{
          bgColor: "#00b8d9",
          borderColor: "#00b8d9",
        }}
      >
        <ToggleBtnGroup.Btn value="generate-postman">
          Generate Postman Collection
        </ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="run-postman">
          Run Postman Collection
        </ToggleBtnGroup.Btn>
      </ToggleBtnGroup>
      {selectedTab === "generate-postman" && (
        <GeneratePostmanCollection
          modelData={modelData}
          closeModal={closeModal}
        />
      )}
      {selectedTab === "run-postman" && (
        <RunPostmanCollection modelData={modelData} closeModal={closeModal} />
      )}
      <SnackBar />
    </>
  );
};

export default ApiValidator;

const GeneratePostmanCollection = ({ modelData, closeModal }: any) => {
  const dispatch = useAppDispatch();
  const uploadData = useAppSelector((state) => state.upload);
  const [input, setInput] = useState<any>(uploadData);
  const [invalid, setInvalid] = useState(true);

  const [isCreatingCollection, setIsCreatingCollection] = useState(false);
  const [collectionData, setCollectionData] = useState("");
  const [isCollectionFileDownloaded, setIsCollectionFileDownloaded] =
    useState(false);

  useEffect(() => {
    try {
      JSON.parse(input);
      setInvalid(false);
      dispatch(
        toastFile({
          message: "JSON Successfully Validated!",
          bgColor: "info",
          isVisible: true,
        })
      );
    } catch (e) {
      setInvalid(true);
      dispatch(
        toastFile({
          message: "Please Upload/Paste Valid JSON File",
          bgColor: "error",
          isVisible: true,
        })
      );
    }
  }, [input]);

  useEffect(() => {
    setInput(JSON.stringify(uploadData, null, 2));
  }, [uploadData]);

  const generatePostmanCollection = async () => {
    const formData = new FormData();
    const blob = new Blob([JSON.stringify(uploadData, null, 2)], {
      type: "application/json",
    });
    const file = new File([blob], "data.json", { type: "application/json" });
    formData.append("apiSpec", file);
    setIsCreatingCollection(true);
    try {
      let response = await fetch(`${BASE_URL}/createPostmanCollection`, {
        method: "POST",
        body: formData,
      });
      const data = await response.json();

      const dataStr =
        "data:text/json;charset=utf-8," +
        encodeURIComponent(JSON.stringify(data));

      setCollectionData(dataStr);
      dispatch(
        toastFile({
          message: "Collection generated successfully! Please download.",
          bgColor: "info",
          isVisible: true,
        })
      );
    } catch (error) {
      console.log(error);
    }
    setIsCreatingCollection(false);
  };

  const downloadCollectionData = () => {
    if (!collectionData) {
      dispatch(
        toastFile({
          message: "Please generate a postman collection.",
          bgColor: "warning",
          isVisible: true,
        })
      );
      return;
    }
    const downloadAnchorNode = document.createElement("a");
    downloadAnchorNode.setAttribute("href", collectionData);
    downloadAnchorNode.setAttribute("download", "collection.json");
    document.body.appendChild(downloadAnchorNode);
    downloadAnchorNode.click();
    downloadAnchorNode.remove();
    setIsCollectionFileDownloaded(true);
    //setCollectionData("");
    dispatch(
      toastFile({
        message: "Collection downloaded successfully!",
        bgColor: "info",
        isVisible: true,
      })
    );
  };

  const handleOnRemoveAll = () => {
    setModelData({
      title: "Are you sure?",
      description:
        "This action will remove properties from the document. Undo will be available.",
      onConfirm: handleClear,
      showModal: true,
    });
  };

  const handleClear = () => {
    setInput("");
    setModelData({
      title: "",
      description: "",
      onConfirm: () => {},
      showModal: false,
    });
  };

  const handleFileSelect = (file: File) => {
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const jsonData = JSON.parse(e.target.result);
        } catch {
          console.log("Something Wents Worng while reading the file");
        }
      };
      reader.readAsText(file);
    }
  };

  const handleSubmit = () => {
    const data = JSON.parse(input);
    dispatch(uploadFile({ ...data }));
    dispatch(exportData({ ...data }));
    //setSelectvalue(true);
  };

  return (
    <div className={styles.container}>
      <div className={`${styles["textarea-container"]}`}>
        <textarea
          onChange={(e) => setInput(e.target.value)}
          value={input}
          className={`${styles["text-area"]}`}
          name="Open Api Json"
          rows={20}
        />
        <div className={`${styles["button-group"]}`}>
          <Button
            round="round"
            color="success"
            className={
              styles[`${collectionData ? "bg-green button1" : "button1"}`]
            }
            disabled={invalid}
            onClick={downloadCollectionData}
            style={{ backgroundColor: `${collectionData ? "green" : ""}` }}
          >
            <span className={styles["button-icon"]}>
              <UploadIcon fill="#FFFFFF" />
            </span>
            {collectionData ? `Download (1)` : `Download`}
          </Button>
          <Button
            round="round"
            className={styles["button1"]}
            onClick={generatePostmanCollection}
          >
            <span className={styles["button-icon"]}>
              {isCreatingCollection ? (
                <LoadingOutlined />
              ) : (
                <PawIcon fill="#FFFFFF" width="16px" height="16px" />
              )}
            </span>
            {isCreatingCollection ? `Generating....` : "Generate"}
          </Button>
          <Button
            round="round"
            className={styles["button"]}
            disabled={!input}
            onClick={handleOnRemoveAll}
          >
            <span className={styles["button-icon"]}>
              <DeleteIcon fill="#FFFFFF" width="16px" height="16px" />
            </span>
            REMOVE ALL
          </Button>
          {modelData.showModal && (
            <Modals
              showModal={modelData.showModal}
              header={modelData.title}
              description={modelData.description}
              onConfirm={modelData.onConfirm}
              onCancel={closeModal}
              childClassName=""
            />
          )}
          <FileUpload
            fileInputClass={styles.fileUpload}
            onFileSelect={handleFileSelect}
            fileType=".json"
          />
        </div>
      </div>
    </div>
  );
};

const RunPostmanCollection = ({ modelData, onCloseModal }: any) => {
  const [isCreatingCollectionReport, setIsCreatingCollectionReport] =
    useState(false);
  const [collectionDataForReport, setCollectionDataForReport] = useState("");
  const [reportDataAsHTMLString, setReportDataAsHTMLString] = useState<any>("");
  const [selectedFileForReport, setSelectedFileForReport] =
    useState("generated");

  const runPostmanCollection = async () => {
    if (!collectionDataForReport && selectedFileForReport === "uploaded") {
      dispatch(
        toastFile({
          message:
            "Please upload a postman collection or select the latest postman collection that was downloaded",
          bgColor: "warning",
          isVisible: true,
        })
      );
      return;
    }
    const formData = new FormData();
    const blob = new Blob([JSON.stringify(collectionDataForReport, null, 2)], {
      type: "application/json",
    });
    const file = new File([blob], "data.json", { type: "application/json" });
    formData.append("postmanCollection", file);
    setIsCreatingCollectionReport(true);
    try {
      let response = await fetch(`${BASE_URL}/runPostmanCollection`, {
        method: "POST",
        body: formData,
      });

      const data = await response.text();
      setReportDataAsHTMLString(data);
    } catch (error) {
      console.log(error);
    }
    setIsCreatingCollectionReport(false);
  };

  const viewHtmlReportInNewTab = () => {
    // Create a new Blob with the HTML content
    const blob = new Blob([reportDataAsHTMLString], { type: "text/html" });
    let url = window.URL.createObjectURL(blob);
    window.open(url, "_blank");
    window.URL.revokeObjectURL(url);
  };

  const downloadReport = () => {
    // Create a new Blob with the HTML content
    const blob = new Blob([reportDataAsHTMLString], { type: "text/html" });
    let url = window.URL.createObjectURL(blob);
    // Create a download link
    let a = document.createElement("a");
    a.href = url;
    a.download = "report.html"; // Name of the file to be downloaded
    a.click(); // Simulate a click on the link to trigger the download
    // Clean up
    window.URL.revokeObjectURL(url);
  };

  const uplodadPostmanCollection = (file: File) => {
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const jsonData = JSON.parse(e.target.result);
          setCollectionDataForReport(jsonData);
        } catch {
          console.log("Something Wents Worng while reading the file");
        }
      };

      reader.readAsText(file);
    }
  };
  return (
    <div className={styles.container}>
      <div className={`${styles["textarea-container"]}`}>
        <div className={styles.runpostman}>
          {isCollectionFileDownloaded === true && (
            <div className={styles.runpostman}>
              <div className={styles.itemCenter}>
                <div onClick={() => setSelectedFileForReport("generated")}>
                  <Radio
                    label="Select Generated Postman Collection"
                    size="sm"
                    checked={selectedFileForReport === "generated"}
                  />
                </div>
                <p
                  style={{
                    marginLeft: "20px",
                    marginTop: "8px",
                    textDecoration: "underline",
                    color: "blue",
                  }}
                >
                  collection.json
                </p>
              </div>
              <p className={styles.itemDist}>Or</p>
            </div>
          )}
          <div className={styles.runpostman}>
            <div className={styles.itemCenter}>
              <div onClick={() => setSelectedFileForReport("uploaded")}>
                <Radio
                  label="Upload Postman Collection"
                  size="sm"
                  checked={selectedFileForReport === "uploaded"}
                />
              </div>

              <FileUpload
                fileInputClass={styles.fileUpload}
                onFileSelect={uplodadPostmanCollection}
                fileType=".json"
              />
            </div>
            <div className={`${styles["button-group1"]}`}>
              <Button
                round="round"
                className={styles["button1"]}
                onClick={runPostmanCollection}
              >
                <span className={styles["button-icon"]}>
                  {isCreatingCollectionReport ? (
                    <LoadingOutlined />
                  ) : (
                    <PawIcon fill="#FFFFFF" width="16px" height="16px" />
                  )}
                </span>
                {isCreatingCollectionReport ? "Running..." : "Run"}
              </Button>
              <Button
                round="round"
                color="success"
                // className={
                //   styles[`${collectionData ? "bg-green button1" : "button1"}`]
                // }
                className={"button1"}
                //disabled={invalid}
                onClick={viewHtmlReportInNewTab}
              >
                <span className={styles["button-icon"]}>
                  <UploadIcon fill="#FFFFFF" />
                </span>
                {reportDataAsHTMLString ? `View Report (1)` : "View Report"}
              </Button>
              <Button
                round="round"
                color="success"
                className={styles["button1"]}
                onClick={downloadReport}
              >
                <span className={styles["button-icon"]}>
                  <UploadIcon fill="#FFFFFF" />
                </span>
                {reportDataAsHTMLString
                  ? `Download Report (1)`
                  : "Download Report"}
              </Button>
            </div>
            {modelData.showModal && (
              <Modals
                showModal={modelData.showModal}
                header={modelData.title}
                description={modelData.description}
                onConfirm={modelData.onConfirm}
                onCancel={onCloseModal}
                childClassName=""
              />
            )}
          </div>
        </div>
      </div>
    </div>
  );
};
