import { Checkbox, InputWithMovingLabel } from "intelli-ui-components-library";
import { updatePath } from "../../mainTabsSlice";
import { useAppDispatch, useAppSelector } from "../../../../store/hooks";
import styles from "./pathsubtabrequest.module.scss";

const PathSubTabRequest = ({ pathName, methodName }: any) => {
  const paths = useAppSelector((state) => state.main.paths);
  const dispatch = useAppDispatch();

  return (
    <div className={styles["mt-6"]}>
      <InputWithMovingLabel
        inputProps={{
          label: "Description",
          name: "requestBody.description",
          value: paths[pathName][methodName].requestBody.description,
          onChange: (event) => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "requestBody",
                data: {
                  ...paths[pathName][methodName].requestBody,
                  description: event.target.value,
                },
              })
            );
          },
          type: "text",
        }}
        top
      />
      <div className={styles["my-4"]}>
        <Checkbox
          checked={paths[pathName][methodName].requestBody.required}
          value="Required"
          onChange={(event) => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "requestBody",
                data: {
                  ...paths[pathName][methodName].requestBody,
                  required: !paths[pathName][methodName].requestBody.required,
                },
              })
            );
          }}
        />
      </div>
      <div>
        {Object.entries(
          paths[pathName][methodName].requestBody.content || {}
        ).map(([key, value]) => (
          <InputWithMovingLabel
            inputProps={{
              label: "Media-Type",
              name: "requestBody.description",
              value: key ?? "",
              onChange: (e) => console.log(e),
              type: "text",
            }}
            top
            className="mb-4"
          />
        ))}
        <button
          onClick={() => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "requestBody",
                data: {
                  ...paths[pathName][methodName].requestBody,
                  content: {
                    "/": {
                      schema: {},
                    },
                    "change/me": {
                      schema: {},
                    },
                  },
                },
              })
            );
          }}
        >
          Add Media
        </button>
      </div>
    </div>
  );
};
export default PathSubTabRequest;
