import React, { useEffect, useState } from "react";
import {
  DataGrid,
  Button,
  Card,
  Checkbox,
  GridColDef,
  IconButton,
  Modal,
} from "intelli-ui-components-library";
import { AddIcon, CopyIcon, DeleteIcon, EditIcon } from "../../assets/icons";
import styles from "./server.module.scss";
import { useSelector } from "react-redux";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import {
  EnumData,
  JsonDetailsObject,
  ServerData,
} from "../../utils/constant/types";
import {
  ServerState,
  addServer,
  deleteSelectedServers,
} from "../../store/reducers/serversSlice";
import { updateData } from "../../store/reducers/uploadSlice";
import TabList from "./TabList";

export interface RowData {
  id?: string;
  selected?: boolean;
  url: string;
  description: string;
  enums: EnumData[];
}

const emptyRowData: RowData = {
  id: "",
  selected: false,
  url: "",
  description: "",
  enums: [],
};

const Server = () => {
  const uploadData = useAppSelector((state) => state.upload.value);
  const dispatch = useAppDispatch();
  const [showModal, setShowModal] = useState(false);
  const [rows, setRows] = useState<any>(
    uploadData.servers.map((obj, index: number) => ({
      ...obj,
      id: `${index}`,
      selected: false,
    }))
  );
  const [editRowData, setEditRowData] = useState<RowData>(emptyRowData);
  useEffect(() => {
    setRows(
      uploadData.servers.map((obj, index: number) => ({
        ...obj,
        id: `${index}`,
        selected: false,
      }))
    );
    setAllRowsSelected(false);
  }, [uploadData.servers]);

  const toggleRowSelection = (id: string) => {
    const updatedRows = rows.map((item) => {
      if (item.id === id) {
        return {
          ...item,
          selected: !item.selected,
        };
      }
      return item;
    });

    setRows(updatedRows);
  };

  const [allRowsSelected, setAllRowsSelected] = useState(false);
  useEffect(() => {
    const updatedRows = rows.map((obj) => ({
      ...obj,
      selected: allRowsSelected,
    }));
    setRows(updatedRows);
  }, [allRowsSelected]);

  const columns: GridColDef[] = [
    {
      field: "selected",
      displayName: "",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div style={{ width: "60px" }}>
          <Checkbox
            checked={row.selected}
            onChange={() => toggleRowSelection(row.id)}
          />
        </div>
      ),
    },
    {
      field: "url",
      displayName: "URL",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div className={styles["url-cell"]}>{`${row.url}`}</div>
      ),
    },
    {
      field: "description",
      displayName: "Description",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div className={styles["desc-cell"]}>{row.description}</div>
      ),
    },
    {
      field: "actions",
      displayName: "Actions",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div className={`${styles["button-group"]}`}>
          <Button
            size="md"
            round="round"
            variant="outline"
            className={styles["buttons"]}
            onClick={() => {
              console.log(row);
              setEditRowData(row);
              setShowModal(true);
            }}
          >
            <EditIcon />
          </Button>
          <Button
            size="md"
            round="round"
            variant="outline"
            className={styles["buttons"]}
          >
            <AddIcon fill="#4b4b4b" />
          </Button>
        </div>
      ),
    },
  ];
  const headerColumns: GridColDef[] = [
    {
      field: "selected",
      displayName: "",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div style={{ width: "60px" }}>
          <Checkbox
            checked={allRowsSelected}
            onChange={() => setAllRowsSelected(!allRowsSelected)}
          />
        </div>
      ),
    },
    {
      field: "url",
      displayName: "URL",
      description: "",
      sortable: false,
      renderCell: () => <div className={styles["url-cell"]}>URL</div>,
    },
    {
      field: "description",
      displayName: "Description",
      description: "",
      sortable: false,
      renderCell: () => <div className={styles["desc-cell"]}>Description</div>,
    },
    {
      field: "actions",
      displayName: "Actions",
      description: "",
      sortable: false,
      renderCell: () => <div>Actions</div>,
    },
  ];
  return (
    <div>
      {showModal && (
        <Modal
          onHidden={() => {
            setShowModal(false);
          }}
          showOverlay={true}
          setting={{
            modalId: "center-btn",
            className: styles["center-btn-ex"],
            variant: "action",
          }}
        >
          <Modal.CloseIcon />
          <Modal.Header>
            {editRowData.url ? "Edit Server Data" : "Add Server Data"}
          </Modal.Header>

          <Modal.Body>
            <TabList
              editRowData={editRowData}
              onClickAddServer={(newServerData) => {
                console.log("....", newServerData);
                if (editRowData.url) {
                  const updatedRows = rows.map((item: RowData) => {
                    if (item.id === newServerData.id) {
                      delete newServerData.id;
                      delete newServerData.selected;
                      return newServerData;
                    } else {
                      delete item.id;
                      delete item.selected;
                      return item;
                    }
                  });
                  dispatch(updateData({ key: "servers", data: updatedRows }));
                  setEditRowData(emptyRowData);
                } else {
                  delete newServerData.id;
                  delete newServerData.selected;
                  dispatch(addServer(newServerData));
                }
                setShowModal(false);
              }}
            />
          </Modal.Body>
        </Modal>
      )}
      <h1 className={styles["main-title"]}>Server</h1>
      <Card className={styles["card"]}>
        <div className={styles["title-section"]}>
          <div>
            <h3 className={styles["card-title"]}>All Servers </h3>
            <h5>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Mollitia,
              molestias
            </h5>
          </div>
          <div className={styles["button-group"]}>
            <Button
              variant="outline"
              round="round"
              className={styles["buttons"]}
              onClick={() => {
                // Delete function finds the non-selected rows and replaces the server array in redux by those rows
                const nonSelectedServers = rows
                  .filter((obj: RowData) => !obj.selected)
                  .map((obj: RowData) => {
                    delete obj.id;
                    delete obj.selected;
                    return obj;
                  });
                dispatch(
                  updateData({ key: "servers", data: nonSelectedServers })
                );
              }}
            >
              <DeleteIcon />
            </Button>
            <Button
              variant="contained"
              className={`${styles["buttons"]} ${styles["add-btn"]}`}
              onClick={() => {
                setShowModal(true);
              }}
            >
              <AddIcon />
            </Button>
          </div>
        </div>
        <>{JSON.stringify(editRowData)}</>

        {rows.length > 0 ? (
          <>
            <DataGrid.Header
              columns={headerColumns}
              className={`${styles["data-grid-header"]}`}
            />
            <DataGrid
              columns={columns}
              rows={rows}
              className={`${styles["data-grid"]}`}
            />
          </>
        ) : (
          <>No Servers Found!</>
        )}
      </Card>
    </div>
  );
};
export default Server;
