import { Checkbox } from 'intelli-ui-components-library';
import styles from './type.module.scss';
import React, { useEffect, useState } from 'react';
import TypeDescription from './TypeDescription';
import { useAppDispatch } from '../../../store/hooks';
import { securityData } from '../../../store/reducers/securitySlice';

interface OAuthTypeProps {
    securityObj:any
    schemeKey:string
}

const OAuthType: React.FC<OAuthTypeProps> = ({securityObj, schemeKey}) => {
    const newSecurityObj = {...securityObj}
    const addSchemeObj = {...newSecurityObj[schemeKey]}
    const dispatch = useAppDispatch();
    const flowObj = {...addSchemeObj.flows};
    const [authSelect, setAuthSelect] = useState<any>({
        implicit: flowObj?.implicit,
        password: flowObj?.password,
        clientCredentials: flowObj?.clientCredentials,
        authorizationCode: flowObj?.authorizationCode
    })

    const [checkList, setCheckList] = useState<any>({
        implicit: false,
        password: false,
        clientCredentials: false,
        authorizationCode: false
    }
    );
    const onChange = (e: any, key: string) => {
        const tempObj = { ...checkList }
        tempObj[key] = e.target.checked
        setCheckList(tempObj);
        tempObj[key]? setAuthSelect({
            ...authSelect,
            [key]: {}
        }) : setAuthSelect({
            ...authSelect,
            [key]: undefined
        })
       
    }
    useEffect(() => {   
        const tempObj = { ...checkList }
        Object.keys(authSelect).map((authKey) =>authSelect[authKey]? tempObj[authKey] = true: false)
        setCheckList(tempObj);
        const newAddSchemeObj = {"type": addSchemeObj.type, "flows":{...authSelect}}
        newSecurityObj[schemeKey] = newAddSchemeObj
        dispatch(securityData({...newSecurityObj}))
    }, [authSelect])
    return (
        <>
            <div className={styles["type-checkbox"]}>
                <div style={{ marginTop: "7px" }}>Flows : </div>
                <div>
                    {
                        Object.keys(checkList).map((key: string) => (
                            <>
                                <Checkbox key={key} value={key} checked={checkList[key]} onChange={(e) => onChange(e, key)} />
                            </>
                        ))
                    }
                </div>
            </div>
            <div>

                {authSelect ? Object.keys(authSelect).map((flow: any) => {
                    return authSelect[flow] ? <TypeDescription securityObj={securityObj} schemeKey={schemeKey} flowKey={flow}/> : ""
                }) : ""

                }

            </div>
        </>
    )
}
export default OAuthType
