import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import demo from "../../utils/constant/demo.json";
import { JsonDetailsObject, ServerData } from "../../utils/constant/types";

interface JsonDetails {
  value: Object;
}

const initialState: JsonDetails = {
  value: {
    openapi: "3.0.0",
    info: {
      title: "Swagger Petstore",
      version: "1.0.0",
      contact: {},
      license: {},
    },
    paths: {},
    externalDocs: {},
    security: [],
    servers: [],
    components: {
      links: {},
      callbacks: {},
      schemas: {},
    },
  },
};

const exportSlice = createSlice({
  name: "export",
  initialState,
  reducers: {
    updateData: (state, action: PayloadAction<JsonDetailsObject>) => {
      const { key, data: payloadData } = action.payload;
      return {
        ...state,
        [key]: payloadData,
      };
    },
  },
});
export const { updateData } = exportSlice.actions;
export const selectDetails = (state: { details: JsonDetails }) =>
  state.details.value;
export default exportSlice.reducer;
