import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import demo from '../../utils/constant/demo.json';

 interface JsonDetails {
    value:Object
 }

 const initialState: JsonDetails = {
    value: {
        "openapi": "3.0.0",
        "info": {
          "title": "Swagger Petstore",
          "version": "1.0.0",
          "contact": {},
          "license": {}
        },
        "paths": {},
        "externalDocs": {},
        "security": [],
        "servers": [],
        "components": {
          "links": {},
          "callbacks": {},
          "schemas": {}
        }
      }
 }

const uploadSlice = createSlice({
    name: "upload",
    initialState,
    reducers: {
        uploadFile: (state, action:PayloadAction<JsonDetails>)=> {
            return {
                ...state,
                value: action.payload
            }
        }
      }

})
export const {uploadFile} = uploadSlice.actions
export const selectDetails = (state: {details:JsonDetails} )=>state.details.value;
export default uploadSlice.reducer;
