import { createSlice, PayloadAction } from "@reduxjs/toolkit";

export type ApiValidatorDetails = any;
const initialState = {
  isCreatingCollection: false,
  collectionData: "",
  hasDownloadedCollection: false,
  fileSelectForReport: "generated",
  collectionDataForReport: "",
  isCreatingReport: false,
  reportDataAsHTMLString: "",
  hasViewedReport: false,
  hasDownloadedReport: false,
};

const apiValidatorSlice = createSlice({
  name: "apiValidator",
  initialState,
  reducers: {
    updateApiValidatorState: (state: any, action: any) => {
      console.log(action.payload);
      return {
        ...state,
        ...action.payload,
      };
    },
  },
});

export const { updateApiValidatorState } = apiValidatorSlice.actions;
export const selectDetails = (state: { details: ApiValidatorDetails }) =>
  state.details;
export default apiValidatorSlice.reducer;
