import { Button, FileUpload } from "intelli-ui-components-library";
import { useEffect, useState } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { uploadFile } from "../../store/reducers/uploadSlice";
import styles from './upload.module.scss';
import { toastFile } from "../../store/reducers/showToastSlice";
import DemoJson from '../../utils/constant/demo.json'
import { DeleteIcon, UploadIcon, PawIcon } from "../../assets/icons";
import SnackBar from "../snackbar/SnackBar";
import Modals from "../modals/Modals";

const Upload = () => {
  const dispatch = useAppDispatch();
  const uploadData = useAppSelector(state => state.upload.value);
  const [input, setInput] = useState(uploadData);
  const [invalid, setInvalid] = useState(true);
  const [modelData, setModelData] = useState({
    title: "",
    description: "",
    onConfirm: () => { },
    showModal: false
  })

  const handleFileSelect = (file: File) => {
    if (file) {
      const reader = new FileReader();
      reader.onload = e => {
        try {
          const jsonData = JSON.parse(e.target.result);
          dispatch(uploadFile(jsonData));
        } catch {
          console.log("Something Wents Worng while reading the file");
        }
      }
      reader.readAsText(file);
    }

  };

  const handleLoadDefault = () => {
    setInput(JSON.stringify(DemoJson, null, 2));
    setInvalid(false);
  };

  const handleSubmit = () => {
    const data = JSON.parse(input);
    dispatch(uploadFile({ value: data }));
  };

  const handleInputChange = (event) => {
    const { value } = event.target;
    setInput(value);
  };

  const handleClear = () => {
    setInput("");
    setModelData({
      title: "",
      description: "",
      onConfirm: () => { },
      showModal: false
    })
  };
  const handleOnRemoveAll = () => {
    setModelData({
      title: "Are you sure?",
      description: "This action will remove properties from the document. Undo will be available.",
      onConfirm: handleClear,
      showModal: true
    })
  }
  const handleOnRemovePaths = () => {
    setModelData({
      title: "Are you sure?",
      description: "This action will remove all paths, operations and parameters. Undo will be available.",
      onConfirm: handleClear,
      showModal: true
    })
  }
  const handleOnCancel = () => {
    setModelData({
      title: "",
      description: "",
      onConfirm: () => { },
      showModal: false
    })
  }
  useEffect(() => {
    try {
      JSON.parse(input);
      setInvalid(false);
      dispatch(toastFile({
        message: "JSON Successfully Validated!",
        bgColor: "info",
        isVisible: true
      }))
    } catch (e) {
      setInvalid(true);
      dispatch(toastFile({
        message: "Please Upload/Paste Valid JSON File",
        bgColor: "error",
        isVisible: true
      }))
    }
  }, [input]);

  useEffect(() => {
    setInput(JSON.stringify(uploadData, null, 2));
  }, [uploadData]);
  return (
    <>
      <div className={styles.container}>
        <div className={`${styles["textarea-container"]}`}>
          <textarea
            onChange={handleInputChange}
            value={input}
            className={`${styles["text-area"]}`}
            name="Open Api Json"
            rows={20}
          />
        </div>
        <div className={`${styles["button-group"]}`}>
          <Button
            round="round"
            color="success"
            className={styles.button1}
            disabled={invalid}
            onClick={handleSubmit}
          >
            <span className={styles["button-icon"]}><UploadIcon fill="#FFFFFF" /></span>LOAD DEFINATION
          </Button>
          <Button round="round" className={styles.button1} onClick={handleLoadDefault}>
            <span className={styles["button-icon"]}><PawIcon fill="#FFFFFF" width="16px" height="16px" /></span>DEMO</Button>
          <Button round="round" className={styles.button} disabled={!input} onClick={handleOnRemovePaths}>
            <span className={styles["button-icon"]}><DeleteIcon fill="#FFFFFF" width="16px" height="16px" /></span>REMOVE PATHS</Button>
          <Button round="round" className={styles.button} disabled={!input} onClick={handleOnRemoveAll}>
            <span className={styles["button-icon"]}><DeleteIcon fill="#FFFFFF" width="16px" height="16px" /></span>REMOVE ALL</Button>
          {modelData.showModal && <Modals
            showModal={modelData.showModal}
            header={modelData.title}
            description={modelData.description}
            onConfirm={modelData.onConfirm}
            onCancel={handleOnCancel}
          />}
          <FileUpload fileInputClass={styles.fileUpload} onFileSelect={handleFileSelect} fileType=".json" />
        </div>
      </div>
      <SnackBar />
    </>
  );
};

export default Upload;
