import { createAsyncThunk, createSlice, current } from "@reduxjs/toolkit";
import { JsonDetailsObject, ServerData } from "../../utils/constant/types";
import { RootState } from "../store";
import { updateData } from "./uploadSlice";

const initialState: ServerData[] = [];

export interface ServerState {
  selected: boolean;
  id: string;
  data: ServerData;
}

export const addServer = createAsyncThunk(
  "servers/addServer",
  async (newServer: ServerState, { getState, dispatch }) => {
    // Access the state using getState()
    const currentState = getState() as RootState;
    const updatedServers = [
      ...currentState.upload.value.servers,
      newServer.data,
    ];
    // Update the 'servers' array in the uploadSlice
    const jsonDetailsObject: JsonDetailsObject = {
      key: "servers",
      data: updatedServers,
    };
    dispatch(updateData(jsonDetailsObject));
  }
);

export const editServer = createAsyncThunk(
  "servers/addServer",
  async (newServer: ServerData, { getState, dispatch }) => {
    // Access the state using getState()
    const currentState = getState() as RootState;
    const updatedServers = [...currentState.upload.value.servers, newServer];
    // Update the 'servers' array in the uploadSlice
    const jsonDetailsObject: JsonDetailsObject = {
      key: "servers",
      data: updatedServers,
    };
    dispatch(updateData(jsonDetailsObject));
  }
);
export const deleteServers = createAsyncThunk(
  "servers/addServer",
  async (selectedServerIds: string[], { getState, dispatch }) => {
    const currentState = getState() as RootState;
    console.log(selectedServerIds);
    let updatedServers = currentState.upload.value.servers.filter(
      (obj: ServerData) => !selectedServerIds.includes(obj.id ?? "")
    );

    const jsonDetailsObject: JsonDetailsObject = {
      key: "servers",
      data: updatedServers,
    };
    dispatch(updateData(jsonDetailsObject));
  }
);

const serversSlice = createSlice({
  name: "serversSlice",
  initialState,
  reducers: {},
});
