import React, { ChangeEvent, useState } from "react";

import {
  Button,
  DropDown,
  Input,
  ToggleBtnGroup,
} from "intelli-ui-components-library";
import style from "./server.module.scss";
import { AddIcon } from "../../assets/icons";
import { EnumData, ServerData } from "../../utils/constant/types";
import { RowData } from "./Server";

function TabList({
  editRowData,
  onClickAddServer,
}: {
  editRowData: RowData;
  onClickAddServer: (obj: any) => void;
}) {
  const [activeTab, setActiveTab] = useState("tab1");

  const handleTab1 = () => {
    // update the state to tab1
    setActiveTab("tab1");
  };

  const handleTab2 = () => {
    // update the state to tab2
    setActiveTab("tab2");
  };

  const handleTab3 = () => {
    // update the state to tab1
    setActiveTab("tab3");
  };

  const [selectedTab, setSelectedTab] = useState("");
  const clickHandle = (e: any, val: any) => {
    setSelectedTab(val);
  };

  const [serverData, setServerData] = useState<RowData>({
    id: editRowData.id ?? "",
    selected: editRowData.selected ?? false,
    url: editRowData.url ?? "",
    description: editRowData.description ?? "",
    enums: editRowData.enums ?? [],
  });

  return (
    <div className={style["tablist-section"]}>
      <ToggleBtnGroup
        onToggle={clickHandle}
        selectedTab={selectedTab}
        shouldSelectByDefault={true}
        borderBottom={true}
        disabled
        className={style["toggle-btn"]}
        styleObj={{ bgColor: "#0086ff", color: "#0086ff" }}
      >
        <ToggleBtnGroup.Btn
          value="tab1"
          className={activeTab === "tab1" ? "active" : ""}
        >
          <div onClick={handleTab1}>URL</div>
        </ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn
          value="tab2"
          className={activeTab === "tab2" ? "active" : ""}
        >
          <div onClick={handleTab2}>Description</div>
        </ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn
          value="tab3"
          className={activeTab === "tab3" ? "active" : ""}
        >
          <div onClick={handleTab3}>Enums</div>
        </ToggleBtnGroup.Btn>
      </ToggleBtnGroup>

      <div className={style["tab-content"]}>
        {activeTab === "tab1" ? (
          <Url
            value={serverData.url}
            handleChange={(val: string) =>
              setServerData({ ...serverData, url: val })
            }
          />
        ) : activeTab === "tab2" ? (
          <Description
            value={serverData.description}
            handleChange={(val: string) =>
              setServerData({ ...serverData, description: val })
            }
          />
        ) : activeTab === "tab3" ? (
          <Enums />
        ) : (
          ""
        )}
      </div>
      <Button
        className={style["done-btn"]}
        onClick={() => onClickAddServer(serverData)}
      >
        Done
      </Button>
    </div>
  );
}

const Url = ({
  value,
  handleChange,
}: {
  value: string;
  handleChange: (val: string) => void;
}) => {
  return (
    <Input
      value={value}
      placeholder={"Enter Url"}
      onChange={(event) => handleChange(event.target.value)}
    />
  );
};
const Description = ({
  value,
  handleChange,
}: {
  value: string;
  handleChange: (val: string) => void;
}) => {
  return (
    <Input
      value={value}
      placeholder={"Enter Description"}
      onChange={(event) => handleChange(event.target.value)}
    />
  );
};

const Enums = ({
  listOfEnums,
  handleChange,
}: {
  listOfEnums: string[];
  handleChange: (val: string) => void;
}) => {
  return <div>Enum List</div>;
};

export default TabList;
