import { Button } from "intelli-ui-components-library";
import { useEffect, useRef, useState } from "react";

const Upload = () => {
  const [input, setinput] = useState<any>("");
  const [invalid, setInvalid] = useState(true);

  const handleLoadDefault = () => {
    setinput(JSON.stringify(defaultValue, null, 2));
    setInvalid(false);
  };

  const handleSubmit = () => {
    const data = JSON.parse(input);
    //dispatch to redux here
    console.log(data);
  };

  const handleInputChange = (event: any) => {
    const { value } = event.target;
    setinput(value);
  };

  const handleClear = () => {
    setinput("");
  };

  useEffect(() => {
    try {
      JSON.parse(input);
      setInvalid(false);
    } catch (e) {
      setInvalid(true);
    }
  }, [input]);
  return (
    <div className="w-full max-w-2xl mx-auto">
      <textarea
        onChange={handleInputChange}
        value={input}
        className="border-2 rounded w-full"
        name="Open Api Json"
        rows={20}
      >
        The cat was playing in the garden.
      </textarea>
      {invalid && Boolean(input) && (
        <div className="bg-red-100 p-2 rounded my-2">Invalid Json</div>
      )}
      <div className="flex flex-col md:flex-row gap-2">
        <Button onClick={handleLoadDefault}>Demo</Button>
        <Button color="error" disabled={!input} onClick={handleClear}>
          Clear
        </Button>
        <Button
          color="success"
          className="md:flex-1"
          disabled={invalid}
          onClick={handleSubmit}
        >
          Load
        </Button>
      </div>
    </div>
  );
};

const defaultValue = {
  openapi: "3.0.0",
  info: {
    title: "API",
    version: "1.0.0",
    contact: {},
    license: {},
  },
  externalDocs: {},
  security: [],
  servers: [],
  paths: {},
  components: {
    links: {},
    callbacks: {},
    schemas: {},
  },
};
export default Upload;
