import ArrowDropDownIcon from "@mui/icons-material/ArrowDropDown";
import ArrowDropUpIcon from "@mui/icons-material/ArrowDropUp";
import React, { useState } from "react";
import { Link, useParams } from "react-router-dom";
import styles from "./sidebar.module.scss";
import SelectionTab from "../SelectionTab";
import { useAppSelector } from "../../store/hooks";
import {
  ChevronDownIcon,
  ChevronUpIcon,
  HomeIcon,
  UploadIcon,
} from "../../assets/icons";
import { SidebarItem, sidebarMenu } from "./config";

// export interface SidebarProps {
//   sidebarMenu: SidebarItem[];
// }
type TMenuObj = {
  parentIndex: number | null;
  childIndex: number | null;
  id: string;
  title: string;
};
const Sidebar = () => {
  const selectedTool = useAppSelector((state) => state);
  const params = useParams();
  console.log("params", params);
  console.log(selectedTool);
  const [menuObj, setMenuObj] = useState<TMenuObj>({
    parentIndex: 0,
    childIndex: null,
    id: "home",
    title: "Home",
  });
  // console.log(menuObj);
  const handleActive = (
    parentIndex: number | null,
    childIndex: number | null,
    id: string,
    title: string
  ) => {
    const dataObj = { ...menuObj };
    if (childIndex || childIndex === 0) {
      dataObj["childIndex"] = childIndex;
    } else {
      if (dataObj.parentIndex === parentIndex) {
        dataObj["parentIndex"] = null;
      } else {
        dataObj["parentIndex"] = parentIndex;
        dataObj["childIndex"] = null;
        dataObj["id"] = id;
        dataObj["title"] = title;
      }
    }
    setMenuObj({ ...dataObj });
  };

  const [showApiDocsSubMenus, setShowApiDocsSubMenus] = useState(false);
  const apiDocGenSubMenus = sidebarMenu.filter((obj) =>
    obj.path.startsWith("/api-doc-gen")
  );
  const [showTestWizardSubMenus, setShowTestWizardSubMenus] = useState(false);
  const testWizardSubMenus = sidebarMenu?.filter((obj) =>
    obj.path.startsWith("/test-wizard")
  );

  const renderSubMenus = (subMenuArr: SidebarItem[]) => {
    return (
      <>
        {subMenuArr.map((cur, index) => {
          return (
            <>
              <Link to={cur.path} key={index}>
                <div
                  className={`${styles["list-holder"]} ${
                    menuObj.parentIndex === index && !cur.isChild
                      ? styles["active"]
                      : null
                  } `}
                  key={index}
                  onClick={() => handleActive(index, null, cur.id, cur.title)}
                >
                  <div className={`${styles["list-main"]}`}>
                    <span className={`${styles["list-icon"]}`}>
                      <cur.icon />
                    </span>
                    {cur.title}
                  </div>

                  {cur.isChild && (
                    <div className={styles["list-arrow"]}>
                      {menuObj.parentIndex === index ? (
                        <ArrowDropUpIcon />
                      ) : (
                        <ArrowDropDownIcon />
                      )}
                    </div>
                  )}
                </div>
              </Link>
              {menuObj.parentIndex === index && cur.isChild
                ? cur.subMenu.map((cur, i) => {
                    return (
                      <Link to={cur.path}>
                        <div
                          className={`${styles["list-sub-menu"]} ${
                            menuObj.childIndex === i ? styles["active"] : null
                          }`}
                          key={i}
                          onClick={() => handleActive(index, i)}
                        >
                          <div
                            className={`${styles["list-main"]} font-16 fs-b`}
                          >
                            {cur.title}
                          </div>
                        </div>
                      </Link>
                    );
                  })
                : null}
            </>
          );
        })}
      </>
    );
  };
  return (
    <div className={styles["sidebar-container"]}>
      <div className={styles["list-container"]}>
        <div className={"list-holder"}>
          <div className={`${styles["list-main"]}`}>
            <span className={`${styles["list-icon"]}`}>
              <HomeIcon />
            </span>
            <span>Home</span>
          </div>
        </div>
        <div
          className={"list-holder"}
          onClick={() => setShowApiDocsSubMenus(!showApiDocsSubMenus)}
        >
          <div className={`${styles["list-main"]}`}>
            <span className={`${styles["list-icon"]}`}>
              <UploadIcon />
            </span>
            <span>APIDoc Generator</span>
            <span>
              {showApiDocsSubMenus ? <ChevronDownIcon /> : <ChevronUpIcon />}
            </span>
          </div>
          {showApiDocsSubMenus && renderSubMenus(apiDocGenSubMenus)}
        </div>
        <div
          className={"list-holder"}
          onClick={() => setShowTestWizardSubMenus(!showTestWizardSubMenus)}
        >
          <div className={`${styles["list-main"]}`}>
            <span className={`${styles["list-icon"]}`}>
              <HomeIcon />
            </span>
            <span>Test Wizard</span>
            <span>
              {showTestWizardSubMenus ? <ChevronDownIcon /> : <ChevronUpIcon />}
            </span>
          </div>
          {showTestWizardSubMenus && renderSubMenus(testWizardSubMenus)}
        </div>
      </div>
      <div className={`${styles["tab-selection"]}`}>
        <h1 className={`${styles["sidebar-section-heading"]}`}>
          {menuObj.title}
        </h1>
        <SelectionTab selected={"apiValidator"} />
      </div>
    </div>
  );
};

export default Sidebar;
