import React, { useEffect, useState } from "react";
import {
  DataGrid,
  Button,
  Card,
  Checkbox,
  GridColDef,
  IconButton,
  Modal,
} from "intelli-ui-components-library";
import {
  AddIcon,
  CopyIcon,
  DeleteIcon,
  EditIcon,
  SaveIcon,
} from "../../assets/icons";
//@ts-ignore
import styles from "./main.module.scss";
import { useSelector } from "react-redux";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import {
  EnumData,
  JsonDetailsObject,
  ServerData,
} from "../../utils/constant/types";
import {
  ServerState,
  addServer,
  deleteSelectedServers,
} from "../../store/reducers/serversSlice";
import { updateData } from "../../store/reducers/exportSlice";
import {
  convertToCustomStructure,
  convertToOriginalFormat,
} from "../../utils/conertData";

import EditMode from "./EditMode";
import {
  deleteMethod,
  deletePath,
  updateAllPaths,
} from "../../store/reducers/mainTabsSlice";

export interface RowData {
  id?: string;
  selected?: boolean;
  url: string;
  description: string;
  enums: EnumData[];
}

const emptyRowData: RowData = {
  id: "",
  selected: false,
  url: "",
  description: "",
  enums: [],
};

const Main = () => {
  const data = useAppSelector((state) => state.upload.paths);
  const paths = useAppSelector((state) => state.main.paths);
  const dispatch = useAppDispatch();
  const [showModal, setShowModal] = useState(false);
  const [rows, setRows] = useState<any>([]);
  const [editRowData, setEditRowData] = useState<any>(emptyRowData);

  useEffect(() => {
    dispatch(updateAllPaths({ data }));
  }, [data]);

  useEffect(() => {
    const formatData = convertToCustomStructure(paths, 2);
    setRows(formatData);
    setAllRowsSelected(false);
  }, [paths]);

  //useEffect(() => () => handleSave(), []); // unmount save the current changes

  const toggleRowSelection = (id: string) => {
    const updatedRows = rows.map((item: any) => {
      if (item.key === id) {
        return {
          ...item,
          selected: !item.selected,
        };
      }
      return item;
    });

    setRows(updatedRows);
  };

  const handleOnHidden = () => {
    setShowModal(false);
    setEditRowData(emptyRowData);
  };

  const handleSingleDelete = (key: string) => () => {
    let newPaths: any = { ...data };
    delete newPaths[key];
    //dispatch(updateData({ key: "paths", data: newPaths }));
    dispatch(deletePath({ pathName: key }));
  };

  const handleCopy = () => {
    let seldata: any = [];
    let newData = rows.map((x: any) => {
      if (x.selected) {
        x.selected = false;
        const key = `${x.key}-copy`;
        //@ts-ignore
        if (!paths?.[key] && !x.key.includes("-copy"))
          seldata.push({ ...x, key });
      }
      return x;
    });
    const cpydata: any = [...newData, ...seldata];
    setRows(cpydata);
    dispatch(
      updateData({ key: "paths", data: convertToOriginalFormat(cpydata) })
    );
  };

  const handleDelete = () => {
    setRows((rows: any) => {
      const deleted = rows.filter((x: any) => !x.selected);
      dispatch(
        updateData({ key: "paths", data: convertToOriginalFormat(deleted) })
      );
      return deleted;
    });
    setAllRowsSelected(false);
  };

  const [allRowsSelected, setAllRowsSelected] = useState(false);
  const handleAllSelect = () => {
    setAllRowsSelected((prev: boolean) => {
      setRows(
        rows.map((obj: RowData) => ({
          ...obj,
          selected: !prev,
        }))
      );
      return !prev;
    });
  };

  const handleSave = () => {
    dispatch(updateData({ key: "paths", data: paths }));
  };

  const columns: GridColDef[] = [
    {
      field: "selected",
      displayName: "",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div style={{ width: "60px" }}>
          <Checkbox
            checked={row.selected}
            onChange={() => toggleRowSelection(row.key)}
          />
        </div>
      ),
    },
    {
      field: "key",
      displayName: "ID",
      description: "",
      sortable: false,
    },
    {
      field: "key",
      displayName: "Path Name",
      description: "",
      sortable: false,
      renderCell: (row) => <div style={{ width: "150px" }}>{row.key}</div>,
    },

    {
      field: "pathDescription",
      displayName: "Path Description",
      description: "",
      sortable: false,
      renderCell: (row) => <div style={{ width: "200px" }}>--</div>,
    },
    {
      field: "operation",
      displayName: "Operation",
      description: "",
      sortable: false,
      renderCell: (row) => {
        return (
          <div style={{ width: "250px" }}>
            {row?.data?.map((str: any, i: any) => (
              <span key={str?.key}>{str?.key}</span>
            ))}
          </div>
        );
      },
    },
    {
      field: "action",
      displayName: "Actions",
      description: "",
      sortable: false,
      renderCell: (row) => {
        return (
          <div style={{ width: "100px", display: "flex" }}>
            <Button
              size="md"
              round="round"
              variant="outline"
              onClick={() => {
                setEditRowData(row);
                setShowModal(true);
              }}
              style={{ marginRight: "5px", height: "40px", border: "none" }}
            >
              <EditIcon />
            </Button>
            <Button
              size="md"
              round="round"
              variant="outline"
              onClick={handleSingleDelete(row.key)}
              style={{ border: "none" }}
            >
              <DeleteIcon />
            </Button>
          </div>
        );
      },
    },
  ];
  const headerColumns: GridColDef[] = [
    {
      field: "selected",
      displayName: "",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div style={{ width: "60px" }}>
          <Checkbox checked={allRowsSelected} onChange={handleAllSelect} />
        </div>
      ),
    },
    {
      field: "id",
      displayName: "ID",
      description: "",
      sortable: false,
      renderCell: (row) => <></>,
    },
    {
      field: "pathName",
      displayName: "Path Name",
      description: "",
      sortable: false,
      renderCell: (row) => <div style={{ width: "150px" }}>Path Name</div>,
    },
    {
      field: "pathDescription",
      displayName: "Path Description",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div style={{ width: "200px" }}>Path Description</div>
      ),
    },
    {
      field: "operation",
      displayName: "Operation",
      description: "",
      sortable: false,
      renderCell: (row) => <div style={{ width: "250px" }}>Operation</div>,
    },
    {
      field: "action",
      displayName: "Actions",
      description: "",
      sortable: false,
      renderCell: (row) => <div style={{ width: "100px" }}>Actions</div>,
    },
  ];
  return (
    <div>
      {showModal && (
        <EditMode Key={editRowData.key} onHidden={handleOnHidden} />
      )}
      {JSON.stringify(rows)}
      <div className={styles["card-container"]}>
        <Card className={styles["card"]}>
          <div className={styles["title-section"]}>
            <div>
              <h3 className={styles["card-title"]}>
                OpenAPI Paths Specification
              </h3>
              <h5>
                Lorem ipsum dolor sit amet consectetur adipisicing elit.
                Mollitia, molestias
              </h5>
            </div>
            <div className={styles["button-group"]}>
              <Button
                variant="outline"
                round="round"
                className={styles["buttons"]}
                onClick={handleCopy}
              >
                <CopyIcon />
              </Button>
              <Button
                variant="outline"
                round="round"
                className={styles["buttons"]}
                onClick={handleDelete}
              >
                <DeleteIcon />
              </Button>
              <Button
                variant="contained"
                className={`${styles["buttons"]} ${styles["add-btn"]}`}
                onClick={() => {
                  setShowModal(true);
                  setEditRowData({});
                }}
              >
                <AddIcon />
              </Button>
            </div>
          </div>
          {rows.length > 0 ? (
            <>
              <DataGrid.Header
                columns={headerColumns}
                className={`${styles["data-grid-header"]}`}
              />
              <DataGrid
                columns={columns}
                rows={rows}
                className={`${styles["data-grid"]}`}
              />
            </>
          ) : (
            <>No Paths Defined yet!</>
          )}
        </Card>
      </div>

      <Button
        onClick={() => {
          handleSave();
        }}
        style={{
          width: "fit-content",
          alignSelf: "center",
          backgroundColor: "#9e9e9e",
          borderRadius: "4px",
        }}
      >
        <span className={styles["button-icon"]}>
          <SaveIcon />
        </span>
        Save
      </Button>
    </div>
  );
};
export default Main;
