import React from "react";
import { useMsal } from "@azure/msal-react";
import { loginRequest } from "../../config/authConfig";
import { useNavigate } from "react-router-dom";
import "./common.scss";
import { MicrosoftLogo } from "../../assets/icons";

/**
 * Renders a drop down button with child buttons for logging in with a popup or redirect
 * Note the [useMsal] package
 */

export const SignInButton = () => {
  const { instance } = useMsal();
  const navigate = useNavigate();

  const handleLogin = async (loginType: string) => {
    if (loginType === "popup") {
      try {
        const data: any = await instance.loginPopup(loginRequest);
        if (data) {
          navigate("/dashboard");
        }
      } catch (e) {
        console.log(e);
      }
    }
  };
  return (
    <button onClick={() => handleLogin("popup")} className="signin-buttons">
      <MicrosoftLogo width={"132px"} />
      <span style={{ marginLeft: "8px" }}>Sign in with Microsoft Account</span>
    </button>
  );
};
