import React, { useEffect, useState } from "react";
import {
  DataGrid,
  Button,
  Card,
  Checkbox,
  GridColDef,
  IconButton,
  Modal,
} from "intelli-ui-components-library";
import { AddIcon, CopyIcon, DeleteIcon, EditIcon } from "../../assets/icons";
import styles from "./server.module.scss";
import { useSelector } from "react-redux";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { JsonDetailsObject, ServerData } from "../../utils/constant/types";
import {
  ServerState,
  addServer,
  deleteSelectedServers,
} from "../../store/reducers/serversSlice";
import { updateData } from "../../store/reducers/uploadSlice";
import TabList from "./TabList";

const Server = () => {
  const uploadData = useAppSelector((state) => state.upload.value);
  const dispatch = useAppDispatch();
  const [showModal, setShowModal] = useState(false);
  const [rows, setRows] = useState<any>([]);
  useEffect(() => {
    setRows(
      uploadData.servers.map((obj) => ({
        ...obj,
        selected: false,
      }))
    );
    setAllRowsSelected(false);
  }, [uploadData.servers]);

  const toggleRowSelection = (id: string) => {
    const updatedRows = rows.map((item) => {
      if (item.id === id) {
        return {
          ...item,
          selected: !item.selected,
        };
      }
      return item;
    });

    setRows(updatedRows);
  };

  const [allRowsSelected, setAllRowsSelected] = useState(false);
  useEffect(() => {
    const updatedRows = rows.map((obj) => ({
      ...obj,
      selected: allRowsSelected,
    }));
    setRows(updatedRows);
  }, [allRowsSelected]);

  //const rows = [{ url: "google.com", desc: "desc" }];
  const columns: GridColDef[] = [
    {
      field: "selected",
      displayName: "",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div style={{ width: "60px" }}>
          <Checkbox
            checked={row.selected}
            onChange={() => toggleRowSelection(row.id)}
          />
        </div>
      ),
    },
    {
      field: "url",
      displayName: "URL",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div className={styles["url-cell"]}>{`${row.url} ${row.id}`}</div>
      ),
    },
    {
      field: "description",
      displayName: "Description",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div className={styles["desc-cell"]}>{row.description}</div>
      ),
    },
    {
      field: "actions",
      displayName: "Actions",
      description: "",
      sortable: false,
      renderCell: () => (
        <div className={`${styles["button-group"]}`}>
          <Button
            size="md"
            round="round"
            variant="outline"
            className={styles["buttons"]}
          >
            <EditIcon /> <span className={styles["icon-btn-text"]}>EDIT</span>
          </Button>
          <Button
            size="md"
            round="round"
            variant="outline"
            className={styles["buttons"]}
          >
            <AddIcon fill="#4b4b4b" />
            <span className={styles["icon-btn-text"]}>ADD VARIABLE</span>
          </Button>
        </div>
      ),
    },
  ];
  const headerColumns: GridColDef[] = [
    {
      field: "selected",
      displayName: "",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div style={{ width: "60px" }}>
          <Checkbox
            checked={allRowsSelected}
            onChange={() => setAllRowsSelected(!allRowsSelected)}
          />
        </div>
      ),
    },
    {
      field: "url",
      displayName: "URL",
      description: "",
      sortable: false,
      renderCell: () => <div className={styles["url-cell"]}>URL</div>,
    },
    {
      field: "description",
      displayName: "Description",
      description: "",
      sortable: false,
      renderCell: () => <div className={styles["desc-cell"]}>Description</div>,
    },
    {
      field: "actions",
      displayName: "Actions",
      description: "",
      sortable: false,
      renderCell: () => <div>Actions</div>,
    },
  ];
  return (
    <div>
      {showModal && (
        <Modal
          onHidden={() => {
            setShowModal(false);
          }}
          showOverlay={true}
          setting={{
            modalId: "center-btn",
            className: styles["center-btn-ex"],
            variant: "action",
          }}
        >
          <Modal.CloseIcon />

          <Modal.Body>
            <TabList />
          </Modal.Body>
        </Modal>
      )}
      <h1 className={styles["main-title"]}>Server</h1>
      <Card className={styles["card"]}>
        <div className={styles["title-section"]}>
          <div>
            <h3 className={styles["card-title"]}>All Servers </h3>
            <h5>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Mollitia,
              molestias
            </h5>
          </div>
          <div className={styles["button-group"]}>
            {/* <Button
              variant="outline"
              round="round"
              className={styles["buttons"]}
            >
              <CopyIcon />{" "}
              <span className={styles["icon-btn-text"]}>DUPLICATE</span>
            </Button> */}
            <Button
              variant="outline"
              round="round"
              className={styles["buttons"]}
              onClick={() => {
                const selectedIds = rows
                  .filter((obj) => obj.selected)
                  .map((obj) => `${obj.id}`);
                dispatch(deleteSelectedServers(selectedIds));
              }}
            >
              <DeleteIcon />
              <span className={styles["icon-btn-text"]}>DELETE</span>
            </Button>
            <Button
              variant="contained"
              className={`${styles["buttons"]} ${styles["add-btn"]}`}
              onClick={() => {
                let newServerData: ServerData = {
                  id: `${rows.length + 1}`,
                  url: "google.com",
                  description: `desc`,
                  default: "",
                  enums: [],
                };
                //setShowModal(true);
                dispatch(addServer(newServerData));
              }}
            >
              <AddIcon />
              <span className={styles["icon-btn-text"]}>ADD</span>
            </Button>
          </div>
        </div>
        {rows.length > 0 ? (
          <>
            <DataGrid.Header
              columns={headerColumns}
              className={`${styles["data-grid-header"]}`}
            />
            <DataGrid
              columns={columns}
              rows={rows}
              className={`${styles["data-grid"]}`}
            />
          </>
        ) : (
          <>No Servers Found!</>
        )}
      </Card>
    </div>
  );
};
export default Server;
