import { createAsyncThunk, createSlice, current } from "@reduxjs/toolkit";
import { ServerData } from "../../utils/constant/types";
import { RootState } from "../store";
import { updateData } from "./uploadSlice";

const initialState: ServerData[] = [];

const serversSlice = createSlice({
  name: "serversSlice",
  initialState,
  reducers: {
    addServer1: (state, action) => {
      console.log("Actions", action);
    },
    editServer: (state, action) => {},
    deleteServer: (state, action) => {},
    deleteAllServers: (state, action) => {},
  },
});

export const addServer = createAsyncThunk(
  "servers/addServer",
  async (newServer: ServerData, { getState, dispatch }) => {
    // Access the state using getState()
    const currentState = getState() as RootState;

    // Update the state of the serversSlice if needed
    console.log(currentState);
    console.log(newServer);
    const updatedServers = [...currentState.upload.value.servers, newServer];
    // Update the 'servers' array in the uploadSlice
    //const updatedServers = [...selectServers(currentState), newServer];
    dispatch(updateData({ key: "servers", data: updatedServers }));
  }
);

export const selectCurrentData = (state: RootState) => state.upload.value;
export const updateServersInUploadSlice = createAsyncThunk(
  "servers/updateServersInUploadSlice",
  async (servers: ServerData[], { dispatch, getState }): Promise<void> => {
    const currentState = getState();
    console.log("in async thunk", currentState);
    dispatch(updateServers(servers));
  }
);

//export const { addServer } = serversSlice.actions;
export const serverDetails = (state: { upload: any }) =>
  state.upload.value.servers;
export default serversSlice.reducer;
