import React from "react";
import { Configuration, PublicClientApplication } from "@azure/msal-browser";
import { useMsal } from "@azure/msal-react";

// MSAL configuration
const configuration: Configuration = {
  auth: {
    clientId: `${process.env.REACT_APP_MS_AUTH_APP_ID}`,
    redirectUri: `${process.env.REACT_APP_MS_AUTH_REDIRECT_URI}`,
    authority: `${process.env.REACT_APP_MS_AUTH_AUTHORITY}`,
  },
  cache: {
    cacheLocation: "sessionStorage",
    storeAuthStateInCookie: true,
  },
};
const publicClientApplication = new PublicClientApplication(configuration);

async function msLogin() {
  try {
    await publicClientApplication.loginPopup({
      scopes: [`${process.env.REACT_APP_MS_AUTH_READ_SCOPE}`],
      prompt: "select_account",
    });
  } catch (error) {
    console.log(error);
  }
}

const LoginMS = () => {
  const { accounts } = useMsal();
  console.log(accounts);

  return <button onClick={msLogin}>Login using Micorsoft</button>;
};

export default LoginMS;
