import React from "react";
import { useMsal } from "@azure/msal-react";
import { loginRequest } from "../../config/authConfig";
import { useNavigate } from "react-router-dom";
import MicrosoftLogo from "../../assets/images/microsoft.jpg";
import "./common.scss";

/**
 * Renders a drop down button with child buttons for logging in with a popup or redirect
 * Note the [useMsal] package
 */

export const SignInButton = () => {
  const { instance } = useMsal();
  const navigate = useNavigate();

  const handleLogin = async (loginType: string) => {
    if (loginType === "popup") {
      try {
        const data: any = await instance.loginPopup(loginRequest);
        if (data) {
          navigate("/dashboard");
        }
      } catch (e) {
        console.log(e);
      }
    }
  };
  return (
    <button onClick={() => handleLogin("popup")} className="signin-buttons">
      <svg
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 23 23"
        width={"22px"}
        height={"22px"}
      >
        <path fill="#f3f3f3" d="M0 0h23v23H0z" />
        <path fill="#f35325" d="M1 1h10v10H1z" />
        <path fill="#81bc06" d="M12 1h10v10H12z" />
        <path fill="#05a6f0" d="M1 12h10v10H1z" />
        <path fill="#ffba08" d="M12 12h10v10H12z" />
      </svg>
      <span style={{ marginLeft: "8px" }}>Continue with Microsoft Account</span>
    </button>
  );
};
