import { Button, FileUpload } from "intelli-ui-components-library";
import { useEffect, useState } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { uploadFile } from "../../store/reducers/uploadSlice";
import styles from './upload.module.scss';
import { toastFile } from "../../store/reducers/showToastSlice";
import DemoJson from '../../utils/constant/demo.json'
import { DeleteOutlined, UploadOutlined, CodeOutlined } from '@ant-design/icons';
import SnackBar from "../snackbar/SnackBar";

const Upload = () => {
  const dispatch = useAppDispatch();
  const uploadData = useAppSelector( state => state.upload.value);
  const [input, setInput] = useState(uploadData);
  const [invalid, setInvalid] = useState(true);
    
  const handleFileSelect = (file: File) => {
    if(file)
    {
      const reader = new FileReader();
      reader.onload = e => {
          try {
              const jsonData = JSON.parse(e.target.result);
              dispatch(uploadFile(jsonData));
          } catch {
            console.log("Something Wents Worng while reading the file");
          }
      }
      reader.readAsText(file);
    }
      
  };

  const handleLoadDefault = () => {
    setInput(JSON.stringify(DemoJson, null, 2));
    setInvalid(false);
  };

  const handleSubmit = () => {
    const data = JSON.parse(input);
    dispatch(uploadFile({value:data}));
  };

  const handleInputChange = (event) => {
    const { value } = event.target;
    setInput(value);
  };

  const handleClear = () => {
    setInput("");
  };
 
  useEffect(() => {
    try {
      JSON.parse(input);
      setInvalid(false);
      dispatch(toastFile({
         message:"JSON Successfully Validated!",
         bgColor:"info",
         isVisible:true
      }))
    } catch (e) {
      setInvalid(true);
      dispatch(toastFile({
         message:"Please Upload/Paste Valid JSON File",
         bgColor:"error",
         isVisible:true
      }))
    }
  }, [input]);

  useEffect(() => {
    setInput(JSON.stringify(uploadData, null, 2));
  },[uploadData]);
  return (
   <>
    <div className={styles.container}>
      <div className={`${styles["button-group"]}`}>
        <FileUpload onFileSelect={handleFileSelect} acceptType=".json"/><br/>
        <Button  round="round" className={styles.button} onClick={handleLoadDefault}>
          <CodeOutlined className={`${styles["button-icon"]}`}/>Demo</Button><br/>
        <Button round="round" className={styles.button} color="error" disabled={!input} onClick={handleClear}>
          <DeleteOutlined className={`${styles["button-icon"]}`}/>Clear</Button><br/>
        <Button
          round="round"
          color="success"
          className={styles.button}
          disabled={invalid}
          onClick={handleSubmit}
        >
          <UploadOutlined className={`${styles["button-icon"]}`}/>Load
        </Button>
      </div>
      <textarea
        onChange={handleInputChange}
        value={input}
        className={`${styles["text-area"]}`}
        name="Open Api Json"
        rows={20}
      />
    </div>
    <div>
     <SnackBar/>
    </div>
  </>
  );
};

export default Upload;
