// @ts-nocheck
import {
  Checkbox,
  DropDown,
  InputWithMovingLabel,
  MultiAutoComplete,
  ToggleBtnGroup,
  AutoComplete,
  Button,
} from "intelli-ui-components-library";
import React, { useEffect, useState } from "react";
import styles from "./maintab.module.scss";
import { convertToOriginalFormat } from "../../utils/conertData";
import { useAppSelector } from "../../store/hooks";
import AddEnum from "./AddEnum";
import CreateParam from "./CreateParam";
import requestTypes, {
  locationTypes,
  dataTypes,
  formatTypes,
} from "../../utils/constant/requestTypes";
import { DeleteIcon, SaveIcon, CopyIcon, AddIcon } from "../../assets/icons";

const EditItem = ({
  data,
  Key,
  handleitemInputChange,
  handleitemCheckChange,
  handleMethodChange,
  handleRequiredChange,
  handleInputIncrementChange,
  handleAddParam,
  handleDeleteParam,
  handleParamAddEnum,
  handleDeleteIcon,
  handleAddEnumInputChange,
  handleAddData,
}: any) => {
  const [selectedTab, setSelectedTab] = useState("main");

  const content = () => {
    switch (selectedTab) {
      case "main":
        return (
          <Main
            Key={Key}
            data={data}
            handleChange={handleMethodChange}
            handleitemInputChange={handleitemInputChange}
          />
        );
      case "desc":
        return (
          <Description
            data={data}
            Key={Key}
            handleitemInputChange={handleitemInputChange}
            handleAddData={handleAddData}
          />
        );
      case "docs":
        return (
          <Docs
            data={data}
            Key={Key}
            handleitemInputChange={handleitemInputChange}
          />
        );
      case "req":
        return (
          <Req
            data={data}
            Key={Key}
            handleitemInputChange={handleitemInputChange}
            handleitemCheckChange={handleitemCheckChange}
            handleAddData={handleAddData}
          />
        );
      case "par":
        return (
          <Params
            data={data}
            Key={Key}
            handleChange={handleMethodChange}
            handleitemInputChange={handleitemInputChange}
            handleRequiredChange={handleRequiredChange}
            handleInputIncrementChange={handleInputIncrementChange}
            handleAddParam={handleAddParam}
            handleDeleteParam={handleDeleteParam}
            handleParamAddEnum={handleParamAddEnum}
            handleDeleteIcon={handleDeleteIcon}
            handleAddEnumInputChange={handleAddEnumInputChange}
          />
        );
      default:
        return <div>default</div>;
    }
  };
  return (
    <div>
      <ToggleBtnGroup
        onToggle={(e: { target: { value: React.SetStateAction<string> } }) =>
          setSelectedTab(e.target.value)
        }
        selectedTab={selectedTab}
        shouldSelectByDefault={true}
        borderBottom={true}
        disabled
        styleObj={{ bgColor: "#0086ff", color: "#0086ff" }}
      >
        <ToggleBtnGroup.Btn value="main">Main</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="desc">Description</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="docs">Docs</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="par">Parameters</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="req">Request Body</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="res">Responses</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="sec">Security</ToggleBtnGroup.Btn>
        {/* <ToggleBtnGroup.Btn value="link">Links</ToggleBtnGroup.Btn> */}
        <ToggleBtnGroup.Btn value="call">Callbacks</ToggleBtnGroup.Btn>
      </ToggleBtnGroup>
      <div>{content()}</div>
    </div>
  );
};

const Main = ({ Key, handleChange, data, handleitemInputChange }: any) => {
  return (
    <div className="mt-6">
      <DropDown
        optionList={requestTypes.map((x) => ({
          label: x,
          key: x,
          prev: data?.key,
          name: ["main", "main"],
        }))}
        label="Method"
        value={data?.key}
        onChange={handleChange}
        className="mb-4"
      />
      <InputWithMovingLabel
        inputProps={{
          label: "OperationId",
          name: "operationId",
          value: data?.data?.operationId || "",
          onChange: handleitemInputChange(data),
          type: "text",
        }}
        top
        className="mb-2"
      />
    </div>
  );
};

const Description = ({
  data,
  Key,
  handleitemInputChange,
  handleAddData,
}: any) => {
  //@ts-ignore
  const tags = useAppSelector((state) => state.export.tags);
  return (
    <div className="mt-6">
      <InputWithMovingLabel
        inputProps={{
          label: "Summary",
          name: "summary",
          value: data?.data?.summary || "",
          onChange: handleitemInputChange(data),
          type: "text",
        }}
        top
      />
      <InputWithMovingLabel
        inputProps={{
          label: "Description",
          name: "description",
          value: data?.data?.description || "",
          onChange: handleitemInputChange(data),
          type: "text",
        }}
        top
        className="my-4"
      />
      <MultiAutoComplete
        options={tags.map((x: any) => ({ ...x, label: x.name }))}
        onSelection={(x) =>
          handleAddData({
            key: data.key,
            name: "tags",
            data: [x.target.value],
          })
        }
        onClose={(x) => console.log(x)}
        inputProps={{
          type: "text",
          label: "Tags",
          placeHolder: "Placeholder",
          onChange: (x) => console.log(x),
        }}
      />
    </div>
  );
};

const Docs = ({ data, Key, handleitemInputChange }: any) => {
  return (
    <div className="mt-6">
      <InputWithMovingLabel
        inputProps={{
          label: "Description",
          name: "externalDocs.description",
          value: data?.data?.externalDocs?.description || "",
          onChange: handleitemInputChange(data),
          type: "text",
        }}
        top
        className="mb-4"
      />
      <InputWithMovingLabel
        inputProps={{
          label: "Url",
          name: "externalDocs.url",
          value: data?.data?.externalDocs?.url || "",
          onChange: handleitemInputChange(data),
          type: "text",
        }}
        top
        className="mb-2"
      />
    </div>
  );
};

const Params = ({
  data,
  Key,
  handleitemInputChange,
  handleChange,
  handleRequiredChange,
  handleInputIncrementChange,
  handleAddParam,
  handleDeleteParam,
  handleParamAddEnum,
  handleDeleteIcon,
  handleAddEnumInputChange,
}: any) => {
  let [count, setCount] = useState(1);
  let [addEnumCount, setAddEnumCount] = useState(0);

  let handleAddCount = () => {
    setCount(count + 1);
  };

  let handleAddEnum = (enumval) => {
    setAddEnumCount(enumval);
  };

  return (
    <>
      <div className={styles.parameterContainer}>
        {!!(data && data?.data && data?.data?.parameters) ? (
          data &&
          data?.data &&
          data?.data?.parameters &&
          data?.data?.parameters.map((params: any, i: number) => (
            <>
              <>
                <div className="text-left w-full">
                  Parameter:
                  <button
                    className="bg-gray-400 rounded-md p-1 disabled:bg-gray-200 hover:bg-gray-500"
                    title="Add"
                    onClick={() => handleAddParam(data, i, handleAddCount)}
                  >
                    <AddIcon fill="#FFFFFF" width="16px" height="16px" />
                  </button>
                  <button
                    className="bg-gray-400 rounded-md p-1 disabled:bg-gray-200 hover:bg-gray-500"
                    title="Duplicate"
                    onClick={() => handleAddParam(data, i, handleAddCount)}
                  >
                    <CopyIcon fill="#FFFFFF" width="16px" height="16px" />
                  </button>
                  <button
                    className="bg-gray-400 rounded-md p-1 disabled:bg-gray-200 hover:bg-gray-500"
                    title="Delete"
                    onClick={() => handleDeleteParam(data, i)}
                  >
                    <DeleteIcon fill="#FFFFFF" width="16px" height="16px" />
                  </button>
                  <span>{i > 0 ? "newParam" : `${params?.name}`}</span>
                </div>
                <div className={styles.inputParamName}>
                  <InputWithMovingLabel
                    inputProps={{
                      label: "Parameter Name",
                      name: ["parameters", "name"],
                      value: params?.name || "",
                      onChange: handleitemInputChange(data, i),
                      type: "text",
                    }}
                    top={true}
                  />
                </div>
                <div className={styles.inputDescription}>
                  <InputWithMovingLabel
                    inputProps={{
                      label: "Description",
                      name: ["parameters", "description"],
                      value: params?.description || "",
                      onChange: handleitemInputChange(data, i),
                      type: "text",
                    }}
                    top
                  />
                </div>
                <div className={styles.twoItemGrid}>
                  <DropDown
                    optionList={locationTypes.map((x) => ({
                      label: x,
                      key: x,
                      prev: data?.key,
                      name: ["parameters", "in"],
                    }))}
                    label="Location"
                    value={params?.in || ""}
                    onChange={handleChange(data, i)}
                    className={styles.dropdownLocation}
                  />
                  <DropDown
                    optionList={dataTypes.map((x) => ({
                      label: x,
                      key: x,
                      prev: data?.key,
                      name: ["parameters", "type"],
                    }))}
                    label="Data Type"
                    value={params?.schema?.type || ""}
                    onChange={handleChange(data, i)}
                    className={styles.dropdownType}
                  />
                </div>
                <div className={styles.twoItemGrid}>
                  <InputWithMovingLabel
                    inputProps={{
                      label: "Default Value",
                      name: ["parameters", "default"],
                      value: params?.default || "",
                      onChange: handleitemInputChange(data, i),
                      type: "text",
                    }}
                    top={true}
                    className={styles.inputDefault}
                  />
                  <div className={styles.autoComplete}>
                    <AutoComplete
                      options={formatTypes.map((x) => ({
                        label: x,
                        key: x,
                        prev: data?.key,
                        name: ["parameters", "format"],
                      }))}
                      inputProps={{
                        label: "Format",
                        onChange: handleitemInputChange(data),
                        value: params?.schema?.format || "",
                        type: "text",
                        name: ["parameters", "format"],
                      }}
                      onClose={() => {}}
                    />
                  </div>
                </div>
                <div className={styles.threeItemGrid}>
                  <Checkbox
                    value="Min.Exclusive"
                    color="info"
                    defaultChecked={params?.minrequired}
                    onChange={() =>
                      handleRequiredChange(
                        params?.minrequired || false,
                        ["parameters", "minrequired"],
                        i
                      )
                    }
                    size="sm"
                    className={styles.checkBox}
                  />
                  <Checkbox
                    value="Max. Exclusive"
                    color="info"
                    defaultChecked={params?.maxrequired}
                    onChange={() =>
                      handleRequiredChange(
                        params?.maxrequired || false,
                        ["parameters", "maxrequired"],
                        i
                      )
                    }
                    size="sm"
                    className={styles.checkBox}
                  />
                  <label htmlFor="minInput">Min Length </label>
                  <input
                    id="minInput"
                    type="number"
                    name={["parameters", "minLen", i]}
                    value={params?.minLen || 0}
                    onChange={handleInputIncrementChange}
                    min={0}
                    max={100}
                    step={1}
                  />
                  <label htmlFor="maxInput">Max Length </label>
                  <input
                    id="maxInput"
                    type="number"
                    name={["parameters", "maxLen", i]}
                    value={params?.maxLen || 0}
                    onChange={handleInputIncrementChange}
                    min={0}
                    max={100}
                    step={1}
                  />
                </div>
                <div className={styles.threeItemGrid}>
                  <div>
                    <Button
                      children="Add Enum"
                      size="md"
                      variant="contained"
                      round="round"
                      color="success"
                      onClick={() =>
                        handleParamAddEnum(handleAddEnum, i, addEnumCount, data)
                      }
                      className="mb-2"
                    />
                    {Array.from(
                      { length: params?.enumValObj?.length },
                      (_unused, index) => index + 1
                    ).map((e, i) => (
                      <AddEnum
                        e={e}
                        i={i}
                        addEnumCount={addEnumCount}
                        setAddEnumCount={setAddEnumCount}
                        handleAddIcon={handleParamAddEnum}
                        handleDeleteIcon={handleDeleteIcon}
                        data={data}
                        handleAddEnum={handleAddEnum}
                        handleAddEnumInputChange={handleAddEnumInputChange}
                        enumValObj={params?.enumValObj}
                      />
                    ))}
                  </div>
                  <div>
                    <label htmlFor="maxInput">Multiple Of </label>
                    <input
                      id="multipleOf"
                      type="number"
                      name={["parameters", "multiOf", i]}
                      value={params?.multiOf || 0}
                      onChange={handleInputIncrementChange}
                      min={0}
                      max={100}
                      step={1}
                    />
                  </div>
                  <div>
                    <Checkbox
                      value="Unique Items"
                      color="info"
                      color="info"
                      defaultChecked={params?.uniqueItems}
                      onChange={() =>
                        handleRequiredChange(
                          params?.uniqueItems || false,
                          ["parameters", "uniqueItems"],
                          i
                        )
                      }
                      size="sm"
                      className={styles.checkBox}
                    />
                  </div>
                </div>
                <div className={styles.threeItemGrid}>
                  {params?.schema?.items?.enum?.map((enumValue, index) => (
                    <InputWithMovingLabel
                      key={index}
                      inputProps={{
                        label: "Enum Value",
                        name: ["parameters", "enum", index],
                        value: i > 0 ? "" : enumValue,
                        onChange: handleitemInputChange(data),
                        type: "text",
                      }}
                      top
                    />
                  ))}
                </div>

                <InputWithMovingLabel
                  inputProps={{
                    label: "Pattern",
                    name: ["parameters", "pattern"],
                    value: params?.pattern || "",
                    onChange: handleitemInputChange(data, i),
                    type: "text",
                  }}
                  top
                  className="mb-4"
                />
                <hr className={styles.horLine}></hr>
              </>
            </>
          ))
        ) : (
          <>
            <CreateParam />
          </>
        )}
      </div>
    </>
  );
};

const Req = ({
  data,
  Key,
  handleitemInputChange,
  handleitemCheckChange,
  handleAddData,
}: any) => {
  return (
    <div className="mt-6">
      <InputWithMovingLabel
        inputProps={{
          label: "Description",
          name: "requestBody.description",
          value: data?.data?.requestBody?.description || "",
          onChange: handleitemInputChange(data),
          type: "text",
        }}
        top
      />
      <div className="w-fit my-4">
        <Checkbox
          checked={Boolean(data?.data?.requestBody?.required)}
          value="Required"
          onChange={handleitemCheckChange(data, "requestBody.required")}
        />
      </div>
      <div>
        {Object.entries(data?.data?.requestBody?.content || {}).map(
          ([key, value]) => (
            <InputWithMovingLabel
              inputProps={{
                label: "Media-Type",
                name: "requestBody.description",
                value: key ?? "",
                onChange: (e) =>
                  handleAddData({
                    key: data.key,
                    name: "requestBody.content",
                    data: { [e.target.value]: { scmeme: {} } },
                  }),
                type: "text",
              }}
              top
              className="mb-4"
            />
          )
        )}

        <button
          onClick={() => {
            console.log(data?.data?.requestBody);
            handleAddData({
              key: data.key,
              name: "requestBody.content",
              data: {
                required: null,
                description: "",
                content: {
                  "/": {
                    schema: {},
                  },
                  "change/me": {
                    schema: {},
                  },
                },
              },
            });
          }}
        >
          Add Media
        </button>
      </div>
    </div>
  );
};

export default EditItem;
