import { Button } from "intelli-ui-components-library";
import { useEffect, useRef, useState } from "react";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { uploadFile } from "../../store/reducers/uploadSlice";
import styles from './upload.module.scss';
import { toastFile } from "../../store/reducers/showToastSlice";

const Upload = () => {
  const dispatch = useAppDispatch();
  const newState = useAppSelector( state => state.upload.value);
  console.log(newState)
  const [input, setinput] = useState(JSON.stringify(newState, null, 2));
  const [invalid, setInvalid] = useState(true);

  const handleLoadDefault = () => {
    setinput(JSON.stringify(newState, null, 2));
    setInvalid(false);
  };

  const handleSubmit = () => {
    const data = JSON.parse(input);
    dispatch(uploadFile({value:data}));
  };

  const handleInputChange = (event) => {
    const { value } = event.target;
    setinput(value);
  };

  const handleClear = () => {
    setinput("");
  };

  useEffect(() => {
    try {
      JSON.parse(input);
      setInvalid(false);
      dispatch(toastFile({
         message:"JSON Successfully Validated!",
         bgColor:"info",
         isVisible:true
      }))
    } catch (e) {
      setInvalid(true);
      dispatch(toastFile({
         message:"Please Upload/Paste Valid JSON File",
         bgColor:"error",
         isVisible:true
      }))
    }
  }, [input]);
  return (
    <div className={styles.container}>
      <div className="">
        <Button  round="round" className={styles.button} onClick={handleLoadDefault}>Demo</Button><br/>
        <Button round="round" className={styles.button} color="error" disabled={!input} onClick={handleClear}>
          Clear
        </Button><br/>
        <Button
          round="round"
          color="success"
          className={styles.button}
          disabled={invalid}
          onClick={handleSubmit}
        >
          Load
        </Button>
      </div>
      <div>
      <textarea
        onChange={handleInputChange}
        value={input}
        className={`${styles["text-area"]}`}
        name="Open Api Json"
        rows={20}
      />
      </div>
    </div>
  );
};

export default Upload;
