import { createSlice } from "@reduxjs/toolkit";

interface EnumDetails {
  value?: string;
}

interface ServerDetails {
  url: string;
  desciption: string;
  default?: string;
  enum?: EnumDetails[];
}

const initialState: ServerDetails[] = [];

const serverSlice = createSlice({
  name: "server",
  initialState,
  reducers: {
    addServer: (state, action) => {
      return {
        ...state,
        value: action.payload,
      };
    },
  },
});
export const { addServer } = serverSlice.actions;
export const serverDetails = (state: { upload: any }) => state.upload.value;
export default serverSlice.reducer;
