import React, { useEffect, useState } from "react";
import {
  DataGrid,
  Button,
  Card,
  Checkbox,
  GridColDef,
  IconButton,
} from "intelli-ui-components-library";
import { AddIcon, CopyIcon, DeleteIcon, EditIcon } from "../../assets/icons";
import styles from "./server.module.scss";
import { useSelector } from "react-redux";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import { ServerData } from "../../utils/constant/types";
import { addServer, deleteServers } from "../../store/reducers/serversSlice";

const Server = () => {
  const uploadData = useAppSelector((state) => state.upload.value);
  const dispatch = useAppDispatch();
  const [rows, setRows] = useState<any>([]);

  const [selectAllRows, setSelectAllRows] = useState(false);
  useEffect(() => {
    const rowData = uploadData.servers.map((obj: ServerData) => ({
      ...obj,
      selected: selectAllRows,
    }));
    setRows(rowData);
  }, [selectAllRows]);

  const handleRowSelection = (row) => {
    console.log(row);
    // const updatedData = rows.map((row) =>
    //   row.id === id ? { ...row, selected: !row.selected } : row
    // );
    // setRows(updatedData);
  };

  //const rows = [{ url: "google.com", desc: "desc" }];
  const columns: GridColDef[] = [
    {
      field: "selected",
      displayName: "",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div style={{ width: "60px" }}>
          <Checkbox
            checked={row.selected}
            onChange={() => handleRowSelection(row)}
          />
        </div>
      ),
    },
    {
      field: "url",
      displayName: "URL",
      description: "",
      sortable: false,
      renderCell: (row) => <div className={styles["url-cell"]}>{row.url}</div>,
    },
    {
      field: "description",
      displayName: "Description",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div className={styles["desc-cell"]}>{row.description}</div>
      ),
    },
    {
      field: "actions",
      displayName: "Actions",
      description: "",
      sortable: false,
      renderCell: () => (
        <div className={`${styles["button-group"]}`}>
          <Button
            size="md"
            round="round"
            variant="outline"
            className={styles["buttons"]}
          >
            <EditIcon /> <span className={styles["icon-btn-text"]}>EDIT</span>
          </Button>
          <Button
            size="md"
            round="round"
            variant="outline"
            className={styles["buttons"]}
          >
            <AddIcon fill="#4b4b4b" />
            <span className={styles["icon-btn-text"]}>ADD VARIABLE</span>
          </Button>
        </div>
      ),
    },
  ];
  const headerColumns: GridColDef[] = [
    {
      field: "selected",
      displayName: "",
      description: "",
      sortable: false,
      renderCell: (row) => (
        <div style={{ width: "60px" }}>
          <Checkbox
            checked={row.selected}
            onChange={() => setSelectAllRows(!selectAllRows)}
          />
        </div>
      ),
    },
    {
      field: "url",
      displayName: "URL",
      description: "",
      sortable: false,
      renderCell: () => <div className={styles["url-cell"]}>URL</div>,
    },
    {
      field: "description",
      displayName: "Description",
      description: "",
      sortable: false,
      renderCell: () => <div className={styles["desc-cell"]}>Description</div>,
    },
    {
      field: "actions",
      displayName: "Actions",
      description: "",
      sortable: false,
      renderCell: () => <div>Actions</div>,
    },
  ];
  return (
    <div>
      <h1 className={styles["main-title"]}>Server</h1>
      <Card className={styles["card"]}>
        <div className={styles["title-section"]}>
          <div>
            <h3 className={styles["card-title"]}>All Servers </h3>
            <h5>
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Mollitia,
              molestias
            </h5>
          </div>
          <div className={styles["button-group"]}>
            {/* <Button
              variant="outline"
              round="round"
              className={styles["buttons"]}
            >
              <CopyIcon />{" "}
              <span className={styles["icon-btn-text"]}>DUPLICATE</span>
            </Button> */}
            <Button
              variant="outline"
              round="round"
              className={styles["buttons"]}
              onClick={() => {
                const selectedServerIds = rows.map((obj) => {
                  if (obj.selected) {
                    return obj.id;
                  }
                });
                dispatch(deleteServers(selectedServerIds));
              }}
            >
              <DeleteIcon />
              <span className={styles["icon-btn-text"]}>DELETE</span>
            </Button>
            <Button
              variant="contained"
              className={`${styles["buttons"]} ${styles["add-btn"]}`}
              onClick={() => {
                let obj: ServerData = {
                  id: `${rows.length + 1}`,
                  url: "mahidhar.com",
                  description: `desc`,
                  default: "",
                  enums: [],
                };
                dispatch(addServer(obj));
              }}
            >
              <AddIcon />
              <span className={styles["icon-btn-text"]}>ADD</span>
            </Button>
          </div>
        </div>
        {rows.length > 0 ? (
          <>
            <DataGrid.Header
              columns={headerColumns}
              className={`${styles["data-grid-header"]}`}
            />
            <DataGrid
              columns={columns}
              rows={rows}
              className={`${styles["data-grid"]}`}
            />
          </>
        ) : (
          <>No Servers Found!</>
        )}
      </Card>
    </div>
  );
};
export default Server;
