// @ts-nocheck
import {
  Checkbox,
  DropDown,
  InputWithMovingLabel,
  MultiAutoComplete,
  ToggleBtnGroup,
  AutoComplete,
  Button,
  Radio,
  Card,
} from "intelli-ui-components-library";
import React, { useEffect, useState } from "react";
import styles from "./maintab.module.scss";
import { convertToOriginalFormat } from "../../utils/conertData";
import { useAppDispatch, useAppSelector } from "../../store/hooks";
import AddEnum from "./AddEnum";
import CreateParam from "./CreateParam";
import CreateCallback from "./CreateCallback";
import requestTypes, {
  locationTypes,
  dataTypes,
  formatTypes,
} from "../../utils/constant/requestTypes";
import { DeleteIcon, SaveIcon, CopyIcon, AddIcon } from "../../assets/icons";
import {
  DEFAULT_PARAM_OBJ,
  addPath,
  updatePath,
} from "../../store/reducers/mainTabsSlice";
import { DEFAULT_Callback_OBJ } from "./mainTabsSlice";

const EditItem = ({ pathName, methodName }: any) => {
  const [selectedTab, setSelectedTab] = useState("main");

  const content = () => {
    switch (selectedTab) {
      case "main":
        return <Main pathName={pathName} methodName={methodName} />;
      case "desc":
        return <Description pathName={pathName} methodName={methodName} />;
      case "docs":
        return <Docs pathName={pathName} methodName={methodName} />;
      case "parameters":
        return <Params pathName={pathName} methodName={methodName} />;
      case "req":
        return <Req pathName={pathName} methodName={methodName} />;
      case "sec":
        return <Sec pathName={pathName} methodName={methodName} />;
      case "parameters":
        return <Params pathName={pathName} methodName={methodName} />;
      case "call":
        return <Callbacks pathName={pathName} methodName={methodName} />;
      default:
        return <div>default</div>;
    }
  };
  return (
    <div>
      <ToggleBtnGroup
        onToggle={(e: { target: { value: React.SetStateAction<string> } }) =>
          setSelectedTab(e.target.value)
        }
        selectedTab={selectedTab}
        shouldSelectByDefault={true}
        borderBottom={true}
        disabled
        styleObj={{ bgColor: "#0086ff", color: "#0086ff" }}
      >
        <ToggleBtnGroup.Btn value="main">Main</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="desc">Description</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="docs">Docs</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="parameters">Parameters</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="req">Request Body</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="res">Responses</ToggleBtnGroup.Btn>
        <ToggleBtnGroup.Btn value="sec">Security</ToggleBtnGroup.Btn>
        {/* <ToggleBtnGroup.Btn value="link">Links</ToggleBtnGroup.Btn> */}
        <ToggleBtnGroup.Btn value="call">Callbacks</ToggleBtnGroup.Btn>
      </ToggleBtnGroup>
      <div>{content()}</div>
    </div>
  );
};

const Main = ({ pathName, methodName }: any) => {
  const paths = useAppSelector((state) => state.main.paths);
  const dispatch = useAppDispatch();

  return (
    <div className="mt-6">
      <DropDown
        optionList={requestTypes.map((x) => ({
          label: x,
          key: x,
          //prev: data?.key,
          name: ["main", "main"],
        }))}
        label="Method"
        value={methodName}
        onChange={(option) => {
          const newMethod = option.key;
          return;
        }}
        disabled={true}
        className="mb-4"
      />
      <InputWithMovingLabel
        inputProps={{
          label: "OperationId",
          name: "operationId",
          value: paths[pathName][methodName].operationId,
          onChange: (event) => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "operationId",
                data: event.target.value,
              })
            );
          },
          type: "text",
        }}
        top
        className="mb-2"
      />
    </div>
  );
};

const Description = ({ pathName, methodName }: any) => {
  //@ts-ignore
  const paths = useAppSelector((state) => state.main.paths);
  const dispatch = useAppDispatch();
  const tags = useAppSelector((state) => state.tags.tags);

  return (
    <div className="mt-6">
      <InputWithMovingLabel
        inputProps={{
          label: "Summary",
          name: "summary",
          value: paths[pathName][methodName].summary,
          onChange: (event) => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "summary",
                data: event.target.value,
              })
            );
          },
          type: "text",
        }}
        top
      />
      <InputWithMovingLabel
        inputProps={{
          label: "Description",
          name: "description",
          value: paths[pathName][methodName].description || "",
          onChange: (event) => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "description",
                data: event.target.value,
              })
            );
          },
          type: "text",
        }}
        top
        className="my-4"
      />
      <MultiAutoComplete
        options={tags.map((x: any) => ({ ...x, label: x.name }))}
        onSelection={(event) => {
          const newTag = event.target.value;
          const updatedTags = [
            ...new Set([...paths[pathName][methodName].tags, newTag]),
          ];

          dispatch(
            updatePath({
              pathName,
              methodName,
              methodKey: "tags",
              data: updatedTags,
            })
          );
        }}
        onClose={(x) => console.log(x)}
        inputProps={{
          type: "text",
          label: "Tags",
          placeHolder: "Placeholder",
          onChange: (event) => console.log(event.target.value),
        }}
      />
    </div>
  );
};

const Docs = ({ pathName, methodName }: any) => {
  const paths = useAppSelector((state) => state.main.paths);
  const dispatch = useAppDispatch();

  return (
    <div className="mt-6">
      <InputWithMovingLabel
        inputProps={{
          label: "Description",
          name: "description",
          value: paths[pathName][methodName].externalDocs.description || "",
          onChange: (event) => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "externalDocs",
                data: {
                  ...paths[pathName][methodName].externalDocs,
                  description: event.target.value,
                },
              })
            );
          },
          type: "text",
        }}
        top
        className="mb-4"
      />
      <InputWithMovingLabel
        inputProps={{
          label: "Url",
          name: "url",
          value: paths[pathName][methodName].externalDocs.url || "",
          onChange: (event) => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "externalDocs",
                data: {
                  ...paths[pathName][methodName].externalDocs,
                  url: event.target.value,
                },
              })
            );
          },
          type: "text",
        }}
        top
        className="mb-2"
      />
    </div>
  );
};

const Params = ({ pathName, methodName }: any) => {
  const paths = useAppSelector((state) => state.main.paths);
  const dispatch = useAppDispatch();
  const parametersArr = paths[pathName][methodName].parameters;
  return (
    <div className={styles.parameterContainer}>
      {parametersArr.length > 0 ? (
        parametersArr.map((params: any, i: number) => (
          <>
            <>
              <div className="text-left w-full">
                Parameter:
                <button
                  className="bg-gray-400 rounded-md p-1 disabled:bg-gray-200 hover:bg-gray-500"
                  title="Add"
                  onClick={() => {
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...paths[pathName][methodName].parameters,
                          DEFAULT_PARAM_OBJ,
                        ],
                      })
                    );
                  }}
                >
