                  onChange={() => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      minRequired: !params.minRequired,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  size="sm"
                  className={styles.checkBox}
                />
                <Checkbox
                  value="Max. Exclusive"
                  color="info"
                  checked={params?.maxRequired}
                  onChange={() => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      maxRequired: !params.maxRequired,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  size="sm"
                  className={styles.checkBox}
                />
                <label htmlFor="minInput">Min Length </label>
                <input
                  id="minInput"
                  type="number"
                  name={["parameters", "minLen", i]}
                  value={params?.minLength || 0}
                  onChange={(event) => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      minLength: event?.target.value,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  min={0}
                  max={100}
                  step={1}
                />
                <label htmlFor="maxInput">Max Length </label>
                <input
                  id="maxInput"
                  type="number"
                  name={["parameters", "maxLen", i]}
                  value={params?.maxLength || 0}
                  onChange={(event) => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      maxLength: event?.target.value,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
                          ...parametersArr.slice(i + 1, parametersArr.length),
                        ],
                      })
                    );
                  }}
                  min={0}
                  max={100}
                  step={1}
                />
              </div>

              <InputWithMovingLabel
                inputProps={{
                  label: "Pattern",
                  name: ["parameters", "pattern"],
                  value: params?.pattern || "",
                  onChange: (event) => {
                    const updatedObjAtIndex = {
                      ...parametersArr[i],
                      pattern: event?.target.value,
                    };
                    dispatch(
                      updatePath({
                        pathName,
                        methodName,
                        methodKey: "parameters",
                        data: [
                          ...parametersArr.slice(0, i),
                          updatedObjAtIndex,
