                        ],
                      })
                    );
                  },
                  type: "text",
                }}
                top
                className="mb-4"
              />

              <div className={styles.threeItemGrid}>
                <div>
                  <Button
                    children="Add Enum"
                    size="md"
                    variant="contained"
                    round="round"
                    color="success"
                    onClick={() => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        schema: {
                          ...parametersArr[i].schema,
                          enum: [
                            ...parametersArr[i].schema.enum,
                            "newEnumValue",
                          ],
                        },
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    }}
                    className="mb-2"
                  />
                </div>
                <div>
                  <label htmlFor="maxInput">Multiple Of </label>
                  <input
                    id="multipleOf"
                    type="number"
                    name={["parameters", "multiOf", i]}
                    value={params?.multiOf || 0}
                    onChange={(event) => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        multiOf: event.target.value,
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    }}
                    min={0}
                    max={100}
                    step={1}
                  />
                </div>
                <div>
                  <Checkbox
                    value="Unique Items"
                    color="info"
                    color="info"
                    defaultChecked={params?.uniqueItems}
                    onChange={(event) => {
                      const updatedObjAtIndex = {
                        ...parametersArr[i],
                        uniqueItems: !params.uniqueItems,
                      };
                      dispatch(
                        updatePath({
                          pathName,
                          methodName,
                          methodKey: "parameters",
                          data: [
                            ...parametersArr.slice(0, i),
                            updatedObjAtIndex,
                            ...parametersArr.slice(i + 1, parametersArr.length),
                          ],
                        })
                      );
                    }}
                    size="sm"
                    className={styles.checkBox}
                  />
                </div>
              </div>

              <div>
                {params.schema.enum.length > 0 &&
                  params.schema.enum.map((enumValue, enumIndex) => (
                    <div className={styles["enum-array"]}>
                      <span
                        onClick={() => {
                          const updatedObjAtIndex = {
                            ...parametersArr[i],
                            schema: {
                              ...parametersArr[i].schema,
                              enum: [
                                ...parametersArr[i].schema.enum,
                                "newEnumValue",
                              ],
                            },
                          };
                          dispatch(
                            updatePath({
                              pathName,
                              methodName,
                              methodKey: "parameters",
                              data: [
                                ...parametersArr.slice(0, i),
                                updatedObjAtIndex,
                                ...parametersArr.slice(
                                  i + 1,
                                  parametersArr.length
                                ),
                              ],
                            })
                          );
                        }}
                        style={{
                          margin: "10px 10px 0px 0px",
                        }}
                      >
                        <AddIcon fill="black" />
                      </span>
                      <span
                        onClick={() => {
                          const filteredEnumArr = params.schema.enum.filter(
                            (el, idx) => idx !== enumIndex
                          );
                          const updatedObjAtIndex = {
                            ...parametersArr[i],
                            schema: {
                              ...parametersArr[i].schema,
                              enum: [...filteredEnumArr],
                            },
                          };
                          dispatch(
                            updatePath({
                              pathName,
                              methodName,
                              methodKey: "parameters",
                              data: [
                                ...parametersArr.slice(0, i),
                                updatedObjAtIndex,
                                ...parametersArr.slice(
                                  i + 1,
                                  parametersArr.length
                                ),
                              ],
                            })
                          );
                        }}
                        style={{
                          margin: "10px 10px 0px 0px",
                        }}
                      >
                        <DeleteIcon width="20" height="20" fill="black" />
                      </span>
                      <InputWithMovingLabel
                        key={enumIndex}
                        inputProps={{
                          label: "Enum Value",
                          name: ["parameters", "enum", enumIndex],
                          value: i > 0 ? "" : enumValue,
                          onChange: (event) => {
                            const enumArray = params.schema.enum;
                            const updatedEnumArr = [
                              ...enumArray.slice(0, enumIndex),
                              event.target.value,
                              ...enumArray.slice(
                                enumIndex + 1,
                                enumArray.length
                              ),
                            ];
                            const updatedObjAtIndex = {
                              ...parametersArr[i],
                              schema: {
                                ...parametersArr[i].schema,
                                enum: [...updatedEnumArr],
                              },
                            };
                            dispatch(
                              updatePath({
                                pathName,
                                methodName,
                                methodKey: "parameters",
                                data: [
                                  ...parametersArr.slice(0, i),
                                  updatedObjAtIndex,
                                  ...parametersArr.slice(
                                    i + 1,
                                    parametersArr.length
                                  ),
                                ],
                              })
                            );
                          },
                          type: "text",
                          className: "enum-input__inner",
                        }}
                        top
                        className="mb-2 enum-input__outer"
                      />
                    </div>
                  ))}
              </div>

              <hr className={styles.horLine}></hr>
            </>
          </>
        ))
      ) : (
        <CreateParam pathName={pathName} methodName={methodName} />
      )}
    </div>
  );
};

const Req = ({ pathName, methodName }: any) => {
  const paths = useAppSelector((state) => state.main.paths);
  const dispatch = useAppDispatch();

  return (
    <div className="mt-6">
      <InputWithMovingLabel
        inputProps={{
          label: "Description",
          name: "requestBody.description",
          value: paths[pathName][methodName].requestBody.description,
          onChange: (event) => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "requestBody",
                data: {
                  ...paths[pathName][methodName].requestBody,
                  description: event.target.value,
                },
              })
            );
          },
          type: "text",
        }}
        top
      />
      <div className="w-fit my-4">
        <Checkbox
          checked={paths[pathName][methodName].requestBody.required}
          value="Required"
          onChange={(event) => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "requestBody",
                data: {
                  ...paths[pathName][methodName].requestBody,
                  required: !paths[pathName][methodName].requestBody.required,
                },
              })
            );
          }}
        />
      </div>
      <div>
        {Object.entries(
          paths[pathName][methodName].requestBody.content || {}
        ).map(([key, value]) => (
          <InputWithMovingLabel
            inputProps={{
              label: "Media-Type",
              name: "requestBody.description",
              value: key ?? "",
              onChange: (e) => console.log(e),
              type: "text",
            }}
            top
            className="mb-4"
          />
        ))}
        <button
          onClick={() => {
            dispatch(
              updatePath({
                pathName,
                methodName,
                methodKey: "requestBody",
                data: {
                  ...paths[pathName][methodName].requestBody,
                  content: {
                    "/": {
                      schema: {},
                    },
                    "change/me": {
                      schema: {},
                    },
                  },
                },
              })
            );
          }}
        >
          Add Media
        </button>
      </div>
    </div>
  );
};

const Sec = ({ pathName, methodName }: any) => {
  const paths = useAppSelector((state) => state.main.paths);
  const securitySchemes = useAppSelector(
    (state) => state.security.securitySchemes
  );
  const dispatch = useAppDispatch();

  const [selectedValue, setSelectedValue] = useState("");

  useEffect(() => {
    if (Object.keys(securitySchemes).length > 0) {
      setSelectedValue("custom");
    }
  }, [securitySchemes]);

  useEffect(() => {
    let updatedSecurityData = [];
    if (selectedValue === "default") {
      updatedSecurityData = [];
    } else if (selectedValue === "disabled") {
      updatedSecurityData = [];
    } else if (selectedValue === "custom") {
      updatedSecurityData = Object.keys(securitySchemes).map((key) => ({
        [key]: "",
      }));
    } else {
      updatedSecurityData = [];
    }
    dispatch(
      updatePath({
        pathName,
        methodName,
        methodKey: "security",
        data: [...paths[pathName][methodName].security, ...updatedSecurityData],
      })
    );
  }, [selectedValue]);

  return (
    <Card className={styles["security-subtab"]}>
      <div className={styles["security-subtab__radio"]}>
        <div onClick={() => setSelectedValue("default")}>
          <Radio
            label={"Use default security"}
            checked={selectedValue === "default"}
          />
        </div>
        <div onClick={() => setSelectedValue("disabled")}>
          <Radio
            label={"Disable security"}
            checked={selectedValue === "disabled"}
          />
        </div>
        <div onClick={() => setSelectedValue("custom")}>
          <Radio
            label={"Use custom security"}
            checked={selectedValue === "custom"}
          />
        </div>
      </div>

      {selectedValue === "custom" &&
      paths[pathName][methodName].security.length > 0 ? (
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "flex-start",
          }}
        >
          <p>Selected Security Schemes</p>
          {paths[pathName][methodName].security.map((obj) => {
            let k = Object.keys(obj)[0];
            let v = obj[k];
            return <span key={k}>{`${k}`}</span>;
          })}
        </div>
      ) : (
        <></>
      )}
    </Card>
  );
};

export default EditItem;
