import React, { ReactNode } from "react";
interface btnProps {
    title: string;
    handleClick: () => void;
}
export interface SnackbarProps {
    /**onCloseClick event occurs when the user clicks on snackbar then snackbar will be removed */
    onCloseClick: () => void;
    /**round props used for shape of the snackbar */
    round?: "round" | "pill" | "flat";
    /**message props used for show the text message of the snackbar */
    message?: string;
    /**isVisible props used for hide and show the snackbar */
    isVisible: boolean;
    /**bgColor props used for change the color of the snackbar like primary, success, etc */
    bgColor?: "primary" | "default" | "success" | "error" | "info" | "warning";
    /**leftIcon props used for add some icon for left side of snackbar */
    leftIcon?: ReactNode;
    /**In hasActionBtn props we can passed title and function of the snackbar */
    hasActionBtn?: btnProps;
    /**height props used to changed the position of height of snackbar */
    height?: string;
    /**autoHide props used for hide and show for animation of the snackbar */
    autoHide?: boolean;
    /**animationTime props used for time of the snackbar */
    animationTime?: number;
    /**duration props used for duration of the snackbar */
    duration?: number;
    /**position props used for position of the snackbar like top, bootom, right, left */
    position?: "top" | "top-right" | "top-left" | "bottom" | "bottom-left" | "bottom-right";
}
/**The SnackBar component is used to show messages like success alerts, error messages, or general information. */
declare const Snackbar: React.FC<SnackbarProps>;
export default Snackbar;
